// src/pages/RoleManagementPage.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/role-management.css'; // Create and use CSS for this page

const RoleManagementPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [role, setRole] = useState('');
  const [vendorId, setVendorId] = useState('');
  const [isVendorIdRequired, setIsVendorIdRequired] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserRole = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:8080/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error('Failed to fetch user');
        }
        const data = await response.json();
        setRole(data.user.role || '');
        setVendorId(data.user.vendorId || '');
        setIsVendorIdRequired(data.user.role === '2'); // Adjust based on role value
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserRole();
  }, [userId]);

  const handleRoleChange = (event) => {
    const newRole = event.target.value;
    setRole(newRole);
    setIsVendorIdRequired(newRole === '2'); // Show vendor_id textbox if role is '2'
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/userrole`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ userId, role, vendorId }),
      });
      if (!response.ok) {
        throw new Error('Failed to update user role');
      }
      navigate('/users'); // Redirect back to users page on success
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="role-management-container">
      <h1 className="title">Manage User Role</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="role">Role:</label>
          <select
            id="role"
            value={role}
            onChange={handleRoleChange}
            required
          >
            <option value="">Select Role</option>
            <option value="1">Role 1</option>
            <option value="2">Role 2</option>
            {/* Add more roles as needed */}
          </select>
        </div>
        {isVendorIdRequired && (
          <div className="form-group">
            <label htmlFor="vendorId">Vendor ID:</label>
            <input
              type="text"
              id="vendorId"
              value={vendorId}
              onChange={(e) => setVendorId(e.target.value)}
              required
            />
          </div>
        )}
        <button type="submit" className="submit-button">Save</button>
      </form>
    </div>
  );
};

export default RoleManagementPage;
