import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/editprofile.css'; // Import the same CSS file for styling

const RoleManagementPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [role, setRole] = useState('');
  const [vendorId, setVendorId] = useState('');
  const [isVendorIdRequired, setIsVendorIdRequired] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [successColor, setSuccessColor] = useState('');

  useEffect(() => {
    const fetchUserRole = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:8080/userroles/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error('Failed to fetch user role');
        }
        const data = await response.json();
        const userRoles = data.user_roles || {};
        setRole(userRoles.roleID ? userRoles.roleID.toString() : ''); // Convert roleID to string
        setVendorId(userRoles.vendorId || ''); // Update this if vendorId is part of the response
        setIsVendorIdRequired(userRoles.roleID === 2); // Check if roleID is 2
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserRole();
  }, [userId]);

  const handleRoleChange = (event) => {
    const newRole = event.target.value;
    setRole(newRole);
    setIsVendorIdRequired(newRole === '2');
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/grantrole/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ role, vendorId }), // Sending role and vendorId
      });
  
      if (!response.ok) {
        const errorResponse = await response.json(); // Get detailed error message
        throw new Error(`Failed to update user role: ${errorResponse.error}`);
      }
  
      setSuccess('User role updated successfully');
      setSuccessColor('green');
      setTimeout(() => {
        setSuccess(null);
        navigate('/users');
      }, 2000);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };
  

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div className="profile-container">
      <h1 className="title">Manage User Role</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <div className={`form-group ${error ? 'error' : ''}`}>
          <label htmlFor="role">Role:</label>
          <select
            id="role"
            value={role}
            onChange={handleRoleChange}
            required
          >
            <option value="">Select Role</option>
            <option value="1">Role 1</option>
            <option value="2">Role 2</option>
            <option value="3">Role 3</option>
          </select>
          {error && <div className="error-message">{error}</div>}
        </div>
        {isVendorIdRequired && (
          <div className={`form-group ${error ? 'error' : ''}`}>
            <label htmlFor="vendorId">Vendor ID:</label>
            <input
              type="text"
              id="vendorId"
              value={vendorId}
              onChange={(e) => setVendorId(e.target.value)}
              required
            />
          </div>
        )}
        {success && (
          <div className="success-message" style={{ color: successColor }}>
            {success}
          </div>
        )}
        <div className="form-buttons">
          <button type="submit" className="btn btn-primary">Save</button>
        </div>
      </form>
    </div>
  );
};

export default RoleManagementPage;
