import React, { useState } from 'react';
import { Routes, Route, BrowserRouter as Router, useLocation } from 'react-router-dom';
import Login from './components/login.js';
import Vendors from './components/vendors.js';
import VendorDetails from './components/vendordetails.js';
import AddVendor from './components/addvendor.js';
import Navbar from './components/navbar.js';
import Sidebar from './components/sidebar.js';
import Profile from './components/editprofile.js';
import UsersPage from './components/userspage.js';
import EditUser from './components/edituser.js';

function App() {
  const location = useLocation();

  // Sample initial data
  const initialVendor = {
    name: 'Vendor Name',
    description: 'Vendor Description',
    imageUrl: 'path/to/vendor/image.png',
  };

  const initialTables = [
    { id: 1, status: 'free' },
    { id: 2, status: 'occupied' },
    // Add more tables as needed
  ];

  const initialItems = [
    { id: 1, name: 'Item 1', price: 10 },
    { id: 2, name: 'Item 2', price: 20 },
    // Add more items as needed
  ];

  const initialAdmins = [
    { id: 1, email: 'admin1@example.com' },
    { id: 2, email: 'admin2@example.com' },
    // Add more admins as needed
  ];

  // State hooks for data and form fields
  const [vendor, setVendor] = useState(initialVendor);
  const [tables, setTables] = useState(initialTables);
  const [items, setItems] = useState(initialItems);
  const [admins, setAdmins] = useState(initialAdmins);
  const [newTableNumber, setNewTableNumber] = useState('');
  const [editTableNumber, setEditTableNumber] = useState('');
  const [editTableStatus, setEditTableStatus] = useState('');
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');
  const [editItemName, setEditItemName] = useState('');
  const [editItemPrice, setEditItemPrice] = useState('');
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [error, setError] = useState('');

  const userRole = '1'; // or '2'
  const currentUserId = 123; // example user ID
  const isCurrentUserAdmin = true; // example admin status
  const defaultImage = 'path/to/default/image.png';

  // Handlers for adding, updating, and removing tables
  const handleAddTable = () => {
    if (newTableNumber.trim() === '') {
      setError('Table number cannot be empty');
      return;
    }
    setTables([...tables, { id: tables.length + 1, number: newTableNumber, status: 'free' }]);
    setNewTableNumber('');
  };

  const handleFreeTable = (tableId) => {
    setTables(tables.map((table) => (table.id === tableId ? { ...table, status: 'free' } : table)));
  };

  const handleOccupyTable = (tableId) => {
    setTables(tables.map((table) => (table.id === tableId ? { ...table, status: 'occupied' } : table)));
  };

  const handleUpdateNeedsService = (tableId, needsService) => {
    setTables(tables.map((table) => (table.id === tableId ? { ...table, needsService } : table)));
  };

  const handleVendorFreeTable = (tableId) => {
    setTables(tables.map((table) => (table.id === tableId ? { ...table, status: 'free' } : table)));
  };

  const handleEditTable = (tableId) => {
    setTables(
      tables.map((table) =>
        table.id === tableId ? { ...table, number: editTableNumber, status: editTableStatus } : table
      )
    );
    setEditTableNumber('');
    setEditTableStatus('');
  };

  const handleRemoveTable = (tableId) => {
    setTables(tables.filter((table) => table.id !== tableId));
  };

  // Handlers for adding, updating, and removing items
  const handleAddItem = () => {
    if (newItemName.trim() === '' || newItemPrice.trim() === '') {
      setError('Item name and price cannot be empty');
      return;
    }
    setItems([...items, { id: items.length + 1, name: newItemName, price: parseFloat(newItemPrice) }]);
    setNewItemName('');
    setNewItemPrice('');
  };

  const handleUpdateItem = (itemId) => {
    setItems(
      items.map((item) => (item.id === itemId ? { ...item, name: editItemName, price: parseFloat(editItemPrice) } : item))
    );
    setEditItemName('');
    setEditItemPrice('');
  };

  const handleRemoveItem = (itemId) => {
    setItems(items.filter((item) => item.id !== itemId));
  };

  // Handlers for adding and removing admins
  const handleAddAdmin = () => {
    if (newAdminEmail.trim() === '') {
      setError('Admin email cannot be empty');
      return;
    }
    setAdmins([...admins, { id: admins.length + 1, email: newAdminEmail }]);
    setNewAdminEmail('');
  };

  const handleRemoveAdmin = (adminId) => {
    setAdmins(admins.filter((admin) => admin.id !== adminId));
  };

  // List of routes where Navbar and Sidebar should not be shown
  const noNavAndSidebarRoutes = ['/signin', '/signup'];

  return (
    <div>
      {!noNavAndSidebarRoutes.includes(location.pathname) && (
        <>
          <Navbar />
          <Sidebar />
        </>
      )}

      <div className="main-content">
        <Routes>
          <Route path="/signin" element={<Login />} />
          <Route path="/" element={<Vendors />} />
          <Route path="/vendors" element={<Vendors />} />
          <Route path="/profile" element={<Profile />} />
          <Route
            path="/vendor/:id"
            element={
              <VendorDetails
                vendor={vendor}
                tables={tables}
                items={items}
                admins={admins}
                userRole={userRole}
                currentUserId={currentUserId}
                isCurrentUserAdmin={isCurrentUserAdmin}
                defaultImage={defaultImage}
                handleAddTable={handleAddTable}
                handleFreeTable={handleFreeTable}
                handleOccupyTable={handleOccupyTable}
                handleUpdateNeedsService={handleUpdateNeedsService}
                handleVendorFreeTable={handleVendorFreeTable}
                handleEditTable={handleEditTable}
                handleRemoveTable={handleRemoveTable}
                handleAddItem={handleAddItem}
                handleUpdateItem={handleUpdateItem}
                handleRemoveItem={handleRemoveItem}
                handleAddAdmin={handleAddAdmin}
                handleRemoveAdmin={handleRemoveAdmin}
                error={error}
              />
            }
          />
          <Route path="/add-vendor" element={<AddVendor />} />
          <Route path="/users" element={<UsersPage />} />
          <Route path="/users/edit/:userId" element={<EditUser />} />
        </Routes>
      </div>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
