import React, { useEffect, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';
import defaultImage from '../css/vendor.jpg';
import { useOrderUpdate } from './OrderUpdateContext';

const Navbar = ({ initialCartItems, onCartItemsChange, refreshCart }) => {
    const [userRole, setUserRole] = useState(null);
    const [isTransparent, setIsTransparent] = useState(false);
    const [cartDropdownVisible, setCartDropdownVisible] = useState(false);
    const [cartItems, setCartItems] = useState(initialCartItems || []);
    const [cartDescription, setCartDescription] = useState('');
    const [initialCartDescription, setInitialCartDescription] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);
    const [removalErrorMessage, setRemovalErrorMessage] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);
    const [totalPrice, setTotalPrice] = useState(0);
    const [totalQuantity, setTotalQuantity] = useState(0);
    const [loadingCartItems, setLoadingCartItems] = useState(false);
    const [loadingCheckout, setLoadingCheckout] = useState(false);
    const navigate = useNavigate();
    const dropdownRef = useRef(null);
    const { setShouldUpdateOrders } = useOrderUpdate();
    const [localQuantities, setLocalQuantities] = useState({});
    const [initialQuantities, setInitialQuantities] = useState({});
    const [cart] = useState(initialCartItems || []);

    useEffect(() => {
        fetchCartItems();
    }, [refreshCart]);

    useEffect(() => {
        const initialQuantities = {};
        cartItems.forEach(item => {
            initialQuantities[item.item_id] = item.quantity;
        });
        setLocalQuantities(initialQuantities);
    }, [cartItems]);

    const updateLocalQuantity = (itemId, newQuantity) => {
        if (newQuantity > 0) {
            setLocalQuantities(prev => ({
                ...prev,
                [itemId]: newQuantity
            }));
        }
    };

    const hasUnsavedChanges = () => {
        return cartDescription !== initialCartDescription || 
               Object.keys(localQuantities).some(itemId => localQuantities[itemId] !== initialQuantities[itemId]);
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;
                if (decodedToken.exp < currentTime) {
                    localStorage.removeItem('token');
                    setUserRole(null);
                } else {
                    setUserRole(decodedToken.userRole);
                    fetchCartItems();
                }
            } catch (error) {
                console.error('Error decoding token:', error);
                localStorage.removeItem('token');
                setUserRole(null);
            }
        }
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            setIsTransparent(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setCartDropdownVisible(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const fetchCartItems = async () => {
        setLoadingCartItems(true);
        const token = localStorage.getItem('token');
        if (!token) return;

        try {
            const [itemsResponse, cartResponse] = await Promise.all([
                fetch('http://localhost:8080/cartitems', {
                    headers: { 'Authorization': `Bearer ${token}` }
                }),
                fetch('http://localhost:8080/carts', {
                    headers: { 'Authorization': `Bearer ${token}` }
                })
            ]);

            if (!itemsResponse.ok || !cartResponse.ok) {
                throw new Error('You must have a table to checkout!');
            }

            const itemsData = await itemsResponse.json();
            setCartItems(itemsData.cart || []);

            const cartData = await cartResponse.json();
            setCartDescription(cartData.cart?.description || '');
            setInitialCartDescription(cartData.cart?.description || '');
            setTotalPrice(cartData.cart?.total_price || 0);
            setTotalQuantity(cartData.cart?.quantity || 0);

        } catch (error) {
            console.error('Error fetching cart items:', error);
        } finally {
            setLoadingCartItems(false);
        }
    };

    const deleteCartItem = async (itemId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            console.log('Item deleted successfully');
            await fetchCartItems();
        } catch (error) {
            setRemovalErrorMessage('Error removing item. Please try again.');
            console.error('Error deleting cart item:', error);
        }
    };

    const unsaveChanges = () => {
        setCartDescription(initialCartDescription);
        setLocalQuantities(initialQuantities);
    };

    const saveCart = async () => {
        const token = localStorage.getItem('token');
        try {
            await fetch(`http://localhost:8080/carts/${cart.cart.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                },
                body: new URLSearchParams({ description: cartDescription }).toString()
            });

            await Promise.all(
                Object.keys(localQuantities).map(async itemId => {
                    const newQuantity = localQuantities[itemId];
                    const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                        method: 'PUT',
                        headers: {
                            'Content-Type': 'application/x-www-form-urlencoded',
                            'Authorization': `Bearer ${token}`
                        },
                        body: new URLSearchParams({ item_id: itemId, quantity: newQuantity }).toString()
                    });

                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                })
            );

            await fetchCartItems();
            setSuccessMessage('Changes saved successfully!');
            setTimeout(() => setSuccessMessage(null), 3000);
        } catch (error) {
            console.error('Error saving changes:', error);
            setErrorMessage('Error saving changes. Please try again.');
        }
    };

    const handleCheckout = async () => {
        setLoadingCheckout(true);
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/checkout', {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.errors ? JSON.stringify(errorData.errors) : 'Network response was not ok');
            }

            console.log('Checkout successful');
            setCartItems([]);
            setShouldUpdateOrders(true);
            window.location.reload();
        } catch (error) {
            console.error('Error during checkout:', error);
            setErrorMessage(error.message);
        } finally {
            setLoadingCheckout(false);
        }
    };

    return (
        <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="center-links">
                <li><Link to="/">Home</Link></li>
            </ul>
            <ul className="end-links">
                {userRole === "1" && (
                    <>
                        <li><Link to="/add-vendor">Add Vendor</Link></li>
                        <li><Link to="/users">Users</Link></li>
                    </>
                )}
                {userRole ? (
                    <>
                        <li><Link to="/profile">Profile</Link></li>
                        <li><Link to="/orders">Orders</Link></li>
                        <li>
                            <button onClick={() => setCartDropdownVisible(prev => !prev)} disabled={loadingCartItems}>
                                {loadingCartItems ? "loading cart items..." : 'Cart'}
                            </button>
                            {cartDropdownVisible && (
                                <div className="cart-dropdown" ref={dropdownRef}>
                                    {loadingCartItems ? (
                                        <div className="spinner"></div>
                                    ) : cartItems.length === 0 ? (
                                        <p>No items in cart</p>
                                    ) : (
                                        <>
                                            <ul className="cart-list">
                                                {cartItems.map(item => (
                                                    <li key={item.item_id} className="cart-item">
                                                        <div className="cart-item-img">
                                                            <img src={item.img || defaultImage} alt={item.name} />
                                                        </div>
                                                        <div className="cart-item-name">{item.name}</div>
                                                        <div className="cart-item-quantity">
                                                            <button onClick={() => updateLocalQuantity(item.item_id, localQuantities[item.item_id] - 1)}>-</button>
                                                            <span>{localQuantities[item.item_id]}</span>
                                                            <button onClick={() => updateLocalQuantity(item.item_id, localQuantities[item.item_id] + 1)}>+</button>
                                                        </div>
                                                        <button onClick={() => deleteCartItem(item.item_id)} className='removebutton'>Remove</button>
                                                    </li>
                                                ))}
                                            </ul>
                                            <div className="cart-summary">
                                                <p>Total Quantity: {totalQuantity}</p>
                                                <p>Total Price: ${totalPrice.toFixed(2)}</p>
                                            </div>
                                            {hasUnsavedChanges() && (
                                                <div>
                                                    <button onClick={saveCart} disabled={loadingCartItems}>
                                                        Save Changes
                                                    </button>
                                                    <button onClick={unsaveChanges} disabled={loadingCartItems}>
                                                        Unsave
                                                    </button>
                                                </div>
                                            )}
                                            {!hasUnsavedChanges() && (
                                                <button onClick={handleCheckout} disabled={loadingCheckout}>
                                                    {loadingCheckout ? 'Processing Checkout...' : 'Checkout'}
                                                </button>
                                            )}
                                            <div className="cart-description">
                                                <textarea
                                                    value={cartDescription}
                                                    onChange={(e) => setCartDescription(e.target.value)}
                                                    placeholder="Write a description for your cart..."
                                                    rows="3"
                                                    maxLength="100"
                                                />
                                                <p>{cartDescription.length}/100</p>
                                            </div>
                                            {errorMessage && <p className="error-message">{errorMessage}</p>}
                                            {successMessage && <p className="success-message">{successMessage}</p>}
                                            {removalErrorMessage && <p className="error-message">{removalErrorMessage}</p>}
                                            <button onClick={() => deleteCart(cart.cart.id)} disabled={loadingCartItems} className='removebutton'>
                                                Delete Cart
                                            </button>
                                        </>
                                    )}
                                </div>
                            )}
                        </li>
                        <li><button onClick={() => { localStorage.removeItem('token'); setUserRole(null); navigate('/signin'); }}>Sign Out</button></li>
                    </>
                ) : (
                    <li><Link to="/signin">Sign In</Link></li>
                )}
            </ul>
        </nav>
    );
};

export default Navbar;
