import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [vendor, setVendor] = useState(null);
    const [admins, setAdmins] = useState([]);
    const [tables, setTables] = useState([]);
    const [items, setItems] = useState([]);
    const [newAdminEmail, setNewAdminEmail] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [userOccupiedTables, setUserOccupiedTables] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);
    const [errorTimeout, setErrorTimeout] = useState(null);
    const imageRef = useRef(null);

    const handleError = (errorMessage) => {
        setError(errorMessage);
        if (errorTimeout) clearTimeout(errorTimeout);
        setErrorTimeout(setTimeout(() => setError(null), 3000));
    };

    const fetchVendorDetails = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'An error occurred while fetching vendor details');
                return;
            }

            const data = await response.json();
            if (data && data.vendor) {
                setVendor(data.vendor);
            } else {
                handleError('Vendor data is undefined or null');
            }
        } catch (error) {
            handleError('An unexpected error occurred while fetching vendor details');
        } finally {
            setLoading(false);
        }
    }, [id]);

    const fetchUserRole = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/me', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to fetch user role');
                return;
            }

            const data = await response.json();
            setUserRole(data.me.user_role);
            setCurrentUserId(data.me.user_info.id);
        } catch (error) {
            handleError('An unexpected error occurred while fetching user role');
        }
    }, []);

    const fetchVendorAdmins = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to fetch vendor admins');
                return;
            }

            const data = await response.json();
            setAdmins(data.vendor_admin || []);
        } catch (error) {
            handleError('An unexpected error occurred while fetching vendor admins');
        }
    }, [id]);

    const fetchVendorTables = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to fetch vendor tables');
                return;
            }

            const data = await response.json();
            setTables(data.tables || []);
        } catch (error) {
            handleError('An unexpected error occurred while fetching vendor tables');
        }
    }, [id]);

    const fetchUserTables = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/usertable', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to fetch user tables');
                return [];
            }

            const data = await response.json();
            return Array.isArray(data.tables) ? data.tables : [];
        } catch (error) {
            handleError('An unexpected error occurred while fetching user tables');
            return [];
        }
    }, []);

    useEffect(() => {
        const fetchData = async () => {
            await fetchVendorDetails();
            await fetchUserRole();
            await fetchVendorAdmins();
            await fetchVendorTables();
            await fetchVendorItems(); 
            const occupiedTables = await fetchUserTables();
            setUserOccupiedTables(occupiedTables);

            if (Array.isArray(occupiedTables)) {
                setTables((prevTables) =>
                    prevTables.map((table) =>
                        occupiedTables.includes(table.id) ? { ...table, customer_id: currentUserId } : table
                    )
                );
            }
        };

        fetchData();
    }, [fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems, fetchUserTables, currentUserId]);

    const handleImageHover = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'pointer';
        }
    };

    const handleImageLeave = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'default';
        }
    };

    const handleImageError = (e) => {
        e.target.src = defaultimage;
    };

    const handleAddAdmin = async () => {
        if (!newAdminEmail) return;

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    Authorization: `Bearer ${token}`,
                },
                body: new URLSearchParams({ Email: newAdminEmail }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to add admin');
                return;
            }

            setNewAdminEmail('');
            fetchVendorAdmins();
        } catch (error) {
            handleError('An unexpected error occurred while adding admin');
        }
    };

    const handleRemoveAdmin = async (adminId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to remove admin');
                return;
            }

            setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
        } catch (error) {
            handleError('An unexpected error occurred while removing admin');
        }
    };

    const handleRemoveTable = async (tableId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to remove table');
                return;
            }

            setTables((prevTables) => prevTables.filter((table) => table.id !== tableId));
        } catch (error) {
            handleError('An unexpected error occurred while removing table');
        }
    };

    const handleEditTable = (tableId) => {
        navigate(`/vendor/${id}/tables/${tableId}/edit`);
    };

    const handleAddTable = () => {
        navigate(`/vendor/${id}/tables/new`);
    };

    const handleAddItem = () => {
        navigate(`/vendor/${id}/items/new`);
    };

    const handleRemoveItem = async (itemId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/items/${itemId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                handleError(errorData.error || 'Failed to remove item');
                return;
            }

            setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
        } catch (error) {
            handleError('An unexpected error occurred while removing item');
        }
    };

    if (loading) return <div>Loading...</div>;

  return (
    <div className="page-container">
      <div className="vendor-details-container">
        <div className="vendor-background">
          <img
            src={vendor?.img || defaultimage}
            alt={vendor?.name || 'Vendor'}
            className="vendor-background-image"
            ref={imageRef}
            onMouseOver={handleImageHover}
            onMouseOut={handleImageLeave}
            onError={handleImageError}
          />
          <div className="vendor-info">
            <h1 className="vendor-name">{vendor?.name}</h1>
            <p className="vendor-description">{vendor?.description || 'No description available'}</p>
          </div>
        </div>
 {/* Tables Section */}
<div className="tables-section card">
  <h2 className="section-title">Tables</h2>
  {userRole === '2' && ( // Only show Add Table button for vendor owners
    <button className="add-button" onClick={handleAddTable}>Add Table</button>
  )}
  <div className="tables-list">
    {tables.map((table) => (
      <div key={table.id} className="table-card">
        <h3 className="table-name">{table.name}</h3>
        <p>Available: {table.is_available ? 'Yes' : 'No'}</p>
        <p>Needs Service: {table.is_needs_service ? 'Yes' : 'No'}</p>
        <div className="table-actions">
          {/* Show Free Table button if the current user occupies this table */}
          {table.customer_id === currentUserId && !table.is_available && (
            <button onClick={() => handleFreeTable(table.id)}>Free Table</button>
          )}
          {/* Show Occupy Table button only if the table is available and the Free Table button is not visible */}
          {table.is_available && !(table.customer_id === currentUserId && !table.is_available) && (
            <button onClick={() => handleOccupyTable(table.id)}>Occupy Table</button>
          )}
          {/* Additional actions for vendor role */}
          {userRole === '2' && !table.is_available && (
            <>
              <button onClick={() => handleVendorFreeTable(table.id)}>Release Table</button>
              <button onClick={() => handleUpdateNeedsService(table.id, false)}>Mark as Not Needs Service</button>
              {/* Only show Edit and Remove buttons for vendor owners */}
              <button onClick={() => handleEditTable(table.id)}>Edit</button>
              <button onClick={() => handleRemoveTable(table.id)}>Remove</button>

            </>
          )}
        </div>
      </div>
    ))}
  </div>
  {error && error.includes('table') && (
    <div className="error-message">{error}</div>
  )}
</div>

{/* Items Section */}
<div className="items-section card">
  <h2 className="section-title">Items</h2>
  {userRole === '2' && ( // Only show Add Item button for vendor owners
    <button className="add-button" onClick={handleAddItem}>Add Item</button>
  )}
  <div className="items-list">
    {items.length > 0 ? (
      items.map((item) => (
        <div key={item.id} className="item-card">
          <h3 className="item-name">{item.name}</h3>
          <p>Price: ${item.price}</p>
          <p>Description: {item.description || 'No description available'}</p>
          {/* Additional actions for vendor role */}
          {userRole === '2' && (
            <>
              <button onClick={() => navigate(`/vendor/${id}/items/${item.id}/edit`)}>Edit</button>
              <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
            </>
          )}
        </div>
      ))
    ) : (
      <p>No items available</p>
    )}
  </div>
  {error && error.includes('item') && (
    <div className="error-message">{error}</div>
  )}
</div>


{/* Admins Section */}
{userRole === '2' && (
  <div className="admins-section card">
    <h2 className="section-title">Admins</h2>
    <div className="add-admin-container">
      <input
        type="email"
        value={newAdminEmail}
        onChange={(e) => setNewAdminEmail(e.target.value)}
        placeholder="Admin email"
        className="admin-input"
      />
      <button onClick={handleAddAdmin} className="admin-button">Add Admin</button>
    </div>
    <ul className="admins-list">
      {admins.map((admin) => (
        <li key={admin.user_id} className="admin-item">
          <span>{admin.email}</span>
          <button onClick={() => handleRemoveAdmin(admin.user_id)}>Remove</button>
        </li>
      ))}
    </ul>
    {error && error.includes('admin') && (
      <div className="error-message">{error}</div>
    )}
  </div>
)}
        </div>
      </div>
  );
}

export default VendorDetails;
