import React, { useEffect, useState } from 'react';
import '../css/orders.css'; // Import the updated CSS

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);

  const token = localStorage.getItem('token');
  const userRole = JSON.parse(token).userRole;
  const userId = JSON.parse(token).id;

  let apiUrl;
  if (userRole === "2") {
    // Vendor owner, fetch orders for their store
    apiUrl = `http://localhost:8080/vendororders/${userId}`;
  } else {
    // Regular user, fetch their orders
    apiUrl = 'http://localhost:8080/orders';
  }

  const fetchOrders = async () => {
    try {
      const response = await fetch(apiUrl, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setOrders(data.orders || []);
    } catch (error) {
      setError('Error fetching orders. Please try again.');
      console.error('Error fetching orders:', error);
    }
  };

  const updateOrderStatus = async (orderId) => {
    try {
      const response = await fetch(`http://localhost:8080/orderscompleted/${orderId}`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      // Update the order status in the local state
      setOrders((prevOrders) => prevOrders.map((order) => {
        if (order.id === orderId) {
          return { ...order, status: 'completed' };
        }
        return order;
      }));
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <div className="orders-container">
      <div className="orders-card">
        <div className="orders-header">Orders</div>
        {error && <p className="error-message">{error}</p>}
        <div className="orders-list">
          {orders.map((order) => (
            <div key={order.id} className="order-item">
              <div className="order-details">
                <strong>Total Cost:</strong> ${order.total_order_cost}
              </div>
              <div className="order-details">
                <strong>Status:</strong> {order.status}
              </div>
              <div className="order-vendor">
                <strong>Vendor Name:</strong> {order.vendor_name}
              </div>
              <div className="order-items">
                <strong>Items:</strong>
                {order.item_names.map((item, index) => (
                  <div key={index} className="order-item-details">
                    <span>{item}</span>
                    <span>${order.item_prices[index]}</span>
                  </div>
                ))}
              </div>
              {userRole === "2" && (
                <button onClick={() => updateOrderStatus(order.id)}>Mark as Completed</button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orders;
