import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [tables, setTables] = useState([]);
  const [items, setItems] = useState([]);
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState(null);
  const imageRef = useRef(null);

  const fetchVendorDetails = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        if (response.status === 401) {
          setError('Unauthorized access. Redirecting to sign-in page...');
          setTimeout(() => navigate('/signin'), 3000);
          return;
        }
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      if (data && data.vendor) {
        setVendor(data.vendor);
      } else {
        throw new Error('Vendor data is undefined or null');
      }
    } catch (error) {
      console.error('Error fetching vendor details:', error);
      setError(error.message || 'Failed to load vendor details');
    } finally {
      setLoading(false);
    }
  }, [id, navigate]);

  const fetchUserRole = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const response = await fetch('http://localhost:8080/me', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          if (response.status === 401) {
            setError('Unauthorized access. Redirecting to sign-in page...');
            setTimeout(() => navigate('/signin'), 3000);
            return;
          }
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setUserRole(data.me.user_role);
      }
    } catch (error) {
      console.error('Error fetching user role:', error);
      setError(error.message || 'Failed to fetch user role');
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  const fetchVendorAdmins = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Fetched admins:', data); // Ensure this logs the correct data
      setAdmins(data.vendor_admin || []);
    } catch (error) {
      console.error('Error fetching vendor admins:', error);
      setError(error.message || 'Failed to fetch vendor admins');
    }
  }, [id]);

  const fetchVendorTables = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      setTables(data.tables || []);
    } catch (error) {
      console.error('Error fetching vendor tables:', error);
      setError(error.message || 'Failed to fetch vendor tables');
    }
  }, [id]);

  const fetchVendorItems = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/items`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await response.json();
      setItems(data.items || []);
    } catch (error) {
      console.error('Error fetching vendor items:', error);
      setError(error.message || 'Failed to fetch vendor items');
    }
  }, [id]);

  useEffect(() => {
    fetchVendorDetails();
    fetchUserRole();
    fetchVendorAdmins();
    fetchVendorTables();
    fetchVendorItems();
  }, [id, navigate, fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems]);

  const handleEditClick = () => {
    navigate(`/edit-vendor/${id}`);
  };

  const handleImageHover = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'pointer';
    }
  };

  const handleImageLeave = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'default';
    }
  };

  const handleImageError = (e) => {
    e.target.src = defaultimage;
  };

  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to add admin');
      }

      setNewAdminEmail('');
      fetchVendorAdmins(); // Refresh admin list
    } catch (error) {
      console.error('Error adding admin:', error);
      setError(error.message || 'Failed to add admin');
    }
  };

  const handleRemoveAdmin = async (adminId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to remove admin');
      }

      // Remove the admin from the list
      setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
    } catch (error) {
      console.error('Error removing admin:', error);
      setError(error.message || 'Failed to remove admin');
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="vendor-details">
      <h1>{vendor.name}</h1>
      <div className="vendor-info">
        <img
          src={vendor.image || defaultimage}
          alt={vendor.name}
          className="vendor-image"
          onError={handleImageError}
          ref={imageRef}
          onMouseOver={handleImageHover}
          onMouseOut={handleImageLeave}
        />
        <div className="vendor-description">
          <p>{vendor.description}</p>
          <p>Address: {vendor.address}</p>
          <button className="edit-button" onClick={handleEditClick}>
            Edit Vendor
          </button>
        </div>
      </div>
      {userRole === '1' && (
        <div className="admin-section">
          <h2>Admins</h2>
          {admins.length > 0 ? (
            <ul className="admins-list">
              {admins.map((admin) => (
                <li key={admin.user_id}>
                  <span>Email: {admin.email && admin.email.trim() ? admin.email : 'N/A'}</span>
                  <button
                    className="remove-admin-button"
                    onClick={() => handleRemoveAdmin(admin.user_id)}
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No admins available</p>
          )}
          <input
            type="email"
            value={newAdminEmail}
            onChange={(e) => setNewAdminEmail(e.target.value)}
            className="form-input"
            placeholder="Enter admin email"
          />
          <button className="add-admin-button" onClick={handleAddAdmin}>Add Admin</button>
        </div>
      )}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
}

export default VendorDetails;
