import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [tables, setTables] = useState([]);
  const [items, setItems] = useState([]);
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState(null);
  const imageRef = useRef(null);

  const fetchVendorDetails = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        if (response.status === 401) {
          setError('Unauthorized access. Redirecting to sign-in page...');
          setTimeout(() => navigate('/signin'), 3000);
          return;
        }
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      if (data && data.vendor) {
        setVendor(data.vendor);
      } else {
        throw new Error('Vendor data is undefined or null');
      }
    } catch (error) {
      console.error('Error fetching vendor details:', error);
      setError('Failed to load vendor details');
    } finally {
      setLoading(false);
    }
  }, [id, navigate]);

  const fetchUserRole = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const response = await fetch('http://localhost:8080/me', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          if (response.status === 401) {
            setError('Unauthorized access. Redirecting to sign-in page...');
            setTimeout(() => navigate('/signin'), 3000);
            return;
          }
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setUserRole(data.me.user_role);
      }
    } catch (error) {
      console.error('Error fetching user role:', error);
    } finally {
      setLoading(false);
    }
  }, [navigate]);
  const fetchVendorTables = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/tables`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch vendor tables');
      }

      const data = await response.json();
      console.log('Fetched tables:', data); // Add this line to debug the response
      setTables(data.tables || []);
    } catch (error) {
      console.error('Error fetching vendor tables:', error);
    }
  }, [id]);

  const fetchVendorAdmins = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch vendor admins');
      }

      const data = await response.json();
      console.log('Fetched admins:', data); // Add this line to debug the response
      setAdmins(data.vendor_admin || []);
    } catch (error) {
      console.error('Error fetching vendor admins:', error);
    }
  }, [id]);


  useEffect(() => {
    fetchVendorDetails();
    fetchUserRole();
    fetchVendorAdmins();
    fetchVendorTables();
    fetchVendorItems();
  }, [id, navigate, fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems]);

  const handleEditClick = () => {
    navigate(`/edit-vendor/${id}`);
  };

  const handleImageHover = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'pointer';
    }
  };

  const handleImageLeave = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'default';
    }
  };

  const handleImageError = (e) => {
    e.target.src = defaultimage;
  };

  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });

      if (!response.ok) {
        throw new Error('Failed to add admin');
      }

      setNewAdminEmail('');
      fetchVendorAdmins(); // Refresh admin list
    } catch (error) {
      console.error('Error adding admin:', error);
      setError('Failed to add admin');
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return (
      <div className="error-overlay">
        <div className="error-box">{error}</div>
      </div>
    );
  }

  if (!vendor) {
    return <div>No vendor details available</div>;
  }

  return (
    <div className="page-container">
      <div className="vendor-details-container">
        <div className="vendor-image-container">
          <img
            src={vendor.img || defaultimage}
            alt={vendor.name || 'Vendor'}
            className="vendor-image"
            ref={imageRef}
            onMouseOver={handleImageHover}
            onMouseOut={handleImageLeave}
            onError={handleImageError}
          />
        </div>
        <div className="vendor-info-container">
          <h1 className="vendor-name">{vendor.name}</h1>
          <p className="vendor-description">{vendor.description || 'No description available'}</p>
          {userRole === '1' && (
        <div className="admin-section">
          <h2>Admins</h2>
          <ul>
            {admins.map((admin) => (
              <li key={admin.id}>
                Email: {admin.email && admin.email.trim() ? admin.email : 'N/A'}
              </li>
            ))}
          </ul>
          <input
            type="email"
            value={newAdminEmail}
            onChange={(e) => setNewAdminEmail(e.target.value)}
            className="form-input"
            placeholder="Enter admin email"
          />
          <button onClick={handleAddAdmin}>Add Admin</button>
        </div>
      )}

          {userRole === '1' && (
            <button className="edit-button" onClick={handleEditClick}>
              Edit Vendor
            </button>
          )}
        </div>
      </div>

      {/* Admin Section */}

      {/* Tables Section */}
      <div className="tables-section">
        <h2>Tables</h2>
        <ul>
          {tables.map((table) => (
            <li key={table.id}>Table {table.id}</li>
          ))}
        </ul>
      </div>

      {/* Items Section */}
      <div className="items-section">
        <h2>Items</h2>
        <ul>
          {items.map((item) => (
            <li key={item.id}>{item.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default VendorDetails;
