import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function AddItemPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [itemName, setItemName] = useState('');
  const [price, setPrice] = useState('');
  const [error, setError] = useState(null);

  const handleAddItem = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/items`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ name: itemName, price: parseFloat(price) }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add item';
        setError(errorMessage);
        return;
      }

      navigate(`/vendor/${id}`);
    } catch (error) {
      setError('An unexpected error occurred while adding item');
    }
  };

  return (
    <div className="add-item-page">
      <h2>Add Item</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleAddItem}>
        <label>
          Item Name:
          <input
            type="text"
            value={itemName}
            onChange={(e) => setItemName(e.target.value)}
            required
          />
        </label>
        <label>
          Price:
          <input
            type="number"
            step="0.01"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </label>
        <button type="submit">Add Item</button>
      </form>
    </div>
  );
}

export default AddItemPage;
