import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
  const { id } = useParams();
  const [vendor, setVendor] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [tables, setTables] = useState([]);
  const [items, setItems] = useState([]);
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [newTableNumber, setNewTableNumber] = useState('');
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState(null);
  const imageRef = useRef(null);

  const fetchVendorDetails = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'An error occurred while fetching vendor details';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      if (data && data.vendor) {
        setVendor(data.vendor);
      } else {
        setError('Vendor data is undefined or null');
      }
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor details');
    } finally {
      setLoading(false);
    }
  }, [id]);

  const fetchUserRole = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const response = await fetch('http://localhost:8080/me', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          const errorMessage = errorData.error || 'Failed to fetch user role';
          setError(errorMessage);
          return;
        }

        const data = await response.json();
        setUserRole(data.me.user_role);
      }
    } catch (error) {
      setError('An unexpected error occurred while fetching user role');
    }
  }, []);

  const fetchVendorAdmins = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor admins';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setAdmins(data.vendor_admin || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor admins');
    }
  }, [id]);

  const fetchVendorTables = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/tables`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor tables';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setTables(data.tables || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor tables');
    }
  }, [id]);

  const fetchVendorItems = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/items`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor items';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setItems(data.items || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor items');
    }
  }, [id]);

  useEffect(() => {
    fetchVendorDetails();
    fetchUserRole();
    fetchVendorAdmins();
    fetchVendorTables();
    fetchVendorItems();
  }, [fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems]);

  const handleEditClick = () => {
    // Redirect to edit vendor page, if needed
  };

  const handleImageHover = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'pointer';
    }
  };

  const handleImageLeave = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'default';
    }
  };

  const handleImageError = (e) => {
    e.target.src = defaultimage;
  };

  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add admin';
        setError(errorMessage);
        return;
      }

      setNewAdminEmail('');
      fetchVendorAdmins(); // Refresh admin list
    } catch (error) {
      setError('An unexpected error occurred while adding admin');
    }
  };

  const handleRemoveAdmin = async (adminId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove admin';
        setError(errorMessage);
        return;
      }

      // Remove the admin from the list
      setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
    } catch (error) {
      setError('An unexpected error occurred while removing admin');
    }
  };

  const handleAddTable = async () => {
    if (!newTableNumber) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/tables`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ number: newTableNumber }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add table';
        setError(errorMessage);
        return;
      }

      setNewTableNumber('');
      fetchVendorTables(); // Refresh table list
    } catch (error) {
      setError('An unexpected error occurred while adding table');
    }
  };

  const handleRemoveTable = async (tableId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/tables/${tableId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove table';
        setError(errorMessage);
        return;
      }

      // Remove the table from the list
      setTables((prevTables) => prevTables.filter((table) => table.id !== tableId));
    } catch (error) {
      setError('An unexpected error occurred while removing table');
    }
  };

  const handleAddItem = async () => {
    if (!newItemName || !newItemPrice) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/items`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ name: newItemName, price: newItemPrice }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add item';
        setError(errorMessage);
        return;
      }

      setNewItemName('');
      setNewItemPrice('');
      fetchVendorItems(); // Refresh item list
    } catch (error) {
      setError('An unexpected error occurred while adding item');
    }
  };

  const handleRemoveItem = async (itemId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/items/${itemId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove item';
        setError(errorMessage);
        return;
      }

      // Remove the item from the list
      setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
    } catch (error) {
      setError('An unexpected error occurred while removing item');
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="vendor-details">
      {error && <div className="error">{error}</div>}
      <h1>Vendor Details</h1>
      {vendor && (
        <>
          <div className="vendor-image" onMouseEnter={handleImageHover} onMouseLeave={handleImageLeave}>
            <img src={vendor.image_url || defaultimage} alt="Vendor" ref={imageRef} onError={handleImageError} />
          </div>
          <div className="vendor-info">
            <h2>{vendor.name}</h2>
            <p>{vendor.description}</p>
            <button onClick={handleEditClick}>Edit Vendor</button>
          </div>
        </>
      )}
      <div className="admins-section">
        <h3>Admins</h3>
        <input
          type="email"
          value={newAdminEmail}
          onChange={(e) => setNewAdminEmail(e.target.value)}
          placeholder="New admin email"
        />
        <button onClick={handleAddAdmin}>Add Admin</button>
        <ul>
          {admins.map((admin) => (
            <li key={admin.user_id}>
              {admin.email}
              {userRole === 'admin' && <button onClick={() => handleRemoveAdmin(admin.user_id)}>Remove</button>}
            </li>
          ))}
        </ul>
      </div>
      <div className="tables-section">
        <h3>Tables</h3>
        <input
          type="number"
          value={newTableNumber}
          onChange={(e) => setNewTableNumber(e.target.value)}
          placeholder="New table number"
        />
        <button onClick={handleAddTable}>Add Table</button>
        <ul>
          {tables.map((table) => (
            <li key={table.id}>
              Table {table.number}
              {userRole === 'admin' && <button onClick={() => handleRemoveTable(table.id)}>Remove</button>}
            </li>
          ))}
        </ul>
      </div>
      <div className="items-section">
        <h3>Items</h3>
        <input
          type="text"
          value={newItemName}
          onChange={(e) => setNewItemName(e.target.value)}
          placeholder="New item name"
        />
        <input
          type="number"
          value={newItemPrice}
          onChange={(e) => setNewItemPrice(e.target.value)}
          placeholder="New item price"
        />
        <button onClick={handleAddItem}>Add Item</button>
        <ul>
          {items.map((item) => (
            <li key={item.id}>
              {item.name} - ${item.price}
              {userRole === 'admin' && <button onClick={() => handleRemoveItem(item.id)}>Remove</button>}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default VendorDetails;
