import React, { useState } from 'react';
import { Routes, Route, BrowserRouter as Router, useLocation } from 'react-router-dom';
import Login from './components/login';
import Vendors from './components/vendors';
import VendorDetails from './components/vendordetails';
import AddVendor from './components/addvendor';
import Navbar from './components/navbar';
import Sidebar from './components/sidebar';
import Profile from './components/editprofile';
import UsersPage from './components/userspage';
import EditUser from './components/edituser';
import EditVendor from './components/editvendor';

function App() {
    const [cartItems, setCartItems] = useState([]);
    const location = useLocation();

    const noNavAndSidebarRoutes = ['/signin', '/signup'];

    const handleAddToCart = async (item, quantity) => {
      const token = localStorage.getItem('token'); // Adjust according to where you store your token

      const payload = new URLSearchParams({
          item_id: item.id, // Ensure you're using the correct item variable
          quantity: quantity,
      }).toString();

      console.log('Payload:', payload);

      try {
          const response = await fetch('http://localhost:8080/cartitems', {
              method: 'POST',
              headers: {
                  'Content-Type': 'application/x-www-form-urlencoded',
                  'Authorization': `Bearer ${token}`, // Include the token in the Authorization header
              },
              body: payload,
          });

          if (!response.ok) {
              const errorData = await response.json(); // Get error details from the response
              throw new Error(`Failed to add item to cart: ${errorData.message || 'Unknown error'}`);
          }

          const data = await response.json();
          console.log('Item added to cart:', data);
          alert('Item added to cart successfully!');
      } catch (error) {
          console.error('Error adding item to cart:', error);
          alert('Failed to add item to cart. Please try again.');
      }
  };

    return (
        <div>
            {!noNavAndSidebarRoutes.includes(location.pathname) && (
                <>
                    <Navbar cartItems={cartItems} />
                    <Sidebar />
                </>
            )}

            <div className="main-content">
                <Routes>
                    <Route path="/signin" element={<Login />} />
                    <Route path="/" element={<Vendors />} />
                    <Route path="/vendors" element={<Vendors />} />
                    <Route path="/profile" element={<Profile />} />
                    <Route path="/vendor/:id" element={<VendorDetails onAddToCart={handleAddToCart} />} />
                    <Route path="/edit-vendor/:id" element={<EditVendor />} />
                    <Route path="/add-vendor" element={<AddVendor />} />
                    <Route path="/users" element={<UsersPage />} />
                    <Route path="/users/edit/:userId" element={<EditUser />} />
                </Routes>
            </div>
        </div>
    );
}

export default function AppWrapper() {
    return (
        <Router>
            <App />
        </Router>
    );
}
