import React, { useState } from 'react';
import { Routes, Route, BrowserRouter as Router, useLocation } from 'react-router-dom';
import Login from './components/login';
import Vendors from './components/vendors';
import VendorDetails from './components/vendordetails';
import AddVendor from './components/addvendor';
import Navbar from './components/navbar';
import Sidebar from './components/sidebar';
import Profile from './components/editprofile';
import UsersPage from './components/userspage';
import EditUser from './components/edituser';
import EditVendor from './components/editvendor';
import { CartProvider } from './components/cartcontext';

function App() {
    const location = useLocation();
    const [cartItems, setCartItems] = useState([]);

    const noNavAndSidebarRoutes = ['/signin', '/signup'];
    const handleAddToCart = (newItems) => {
      setCartItems((prevItems) => [...prevItems, ...newItems]);
    };

    return (
        <div>
            {!noNavAndSidebarRoutes.includes(location.pathname) && (
                <>
                    <Navbar cartItems={cartItems} />
                    <Sidebar />
                </>
            )}

            <div className="main-content">
                <Routes>
                    <Route path="/signin" element={<Login />} />
                    <Route path="/" element={<Vendors />} />
                    <Route path="/vendors" element={<Vendors />} />
                    <Route path="/profile" element={<Profile />} />
                    <Route path="/vendor/:id" element={<VendorDetails/>} />
                    <Route path="/edit-vendor/:id" element={<EditVendor />} />
                    <Route path="/add-vendor" element={<AddVendor />} />
                    <Route path="/users" element={<UsersPage />} />
                    <Route path="/users/edit/:userId" element={<EditUser />} />
                </Routes>
            </div>
        </div>
    );
}

export default function AppWrapper() {
    return (
        <Router>
                  <CartProvider>

            <App />
            </CartProvider>

        </Router>
    );
}
