import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode'; // Corrected import
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';
import defaultImage from '../css/vendor.jpg';

const Navbar = ({ initialCartItems, onCartItemsChange }) => {
  const [userRole, setUserRole] = useState(null);
  const [isTransparent, setIsTransparent] = useState(false);
  const [cartDropdownVisible, setCartDropdownVisible] = useState(false);
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState(initialCartItems || []);
  const [updating, setUpdating] = useState(false);

  useEffect(() => {
    fetchCartItems();
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        const currentTime = Date.now() / 1000;
        if (decodedToken.exp < currentTime) {
          localStorage.removeItem('token');
          setUserRole(null);
        } else {
          setUserRole(decodedToken.userRole);
          fetchCartItems();
        }
      } catch (error) {
        console.error('Error decoding token:', error);
        localStorage.removeItem('token');
        setUserRole(null);
      }
    }
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      setIsTransparent(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const fetchCartItems = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8080/cartitems', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setCartItems(data.cart || []);
      if (onCartItemsChange) {
        onCartItemsChange(data.cart || []); // Call the callback with new cart items
      }
    } catch (error) {
      console.error('Error fetching cart items:', error);
    }
  };

  const handleSignOut = () => {
    localStorage.removeItem('token');
    setUserRole(null);
    navigate('/signin');
  };

  const toggleCartDropdown = () => {
    setCartDropdownVisible(!cartDropdownVisible);
  };

  const updateCartItemQuantity = async (itemId, newQuantity) => {
    if (newQuantity < 1) return;

    setUpdating(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': `Bearer ${token}`
        },
        body: new URLSearchParams({ item_id: itemId, quantity: newQuantity }).toString()
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Error response from server:', errorData);
        throw new Error('Network response was not ok');
      }

      fetchCartItems();
    } catch (error) {
      console.error('Error updating cart item:', error);
    } finally {
      setUpdating(false);
    }
  };

  const deleteCartItem = async (itemId) => {
    setCartItems(prevItems => prevItems.filter(item => item.item_id !== itemId));

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          'Authorization': `Bearer ${token}`,
        },
        body: new URLSearchParams({ quantity: 0 }).toString()
      });

      if (!response.ok) {
        const errorData = await response.json();
        console.error('Error response from server:', errorData);
        throw new Error('Network response was not ok');
      }

      console.log('Item deleted successfully');
    } catch (error) {
      console.error('Error deleting cart item:', error);
      setCartItems(prevItems => [...prevItems, { item_id: itemId }]);
    }
  };

  const handleCheckout = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8080/checkout', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      console.log('Checkout successful');
      setCartItems([]);
      if (onCartItemsChange) {
        onCartItemsChange([]); // Call the callback with empty cart items
      }
    } catch (error) {
      console.error('Error during checkout:', error);
    }
  };

  return (
    <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
      <div className="logo">
        <img src={logo} alt="Logo" />
      </div>
      <ul className="center-links">
        <li><Link to="/">Home</Link></li>
      </ul>
      <ul className="end-links">
        {userRole === "1" && (
          <>
            <li><Link to="/add-vendor">Add Vendor</Link></li>
            <li><Link to="/users">Users</Link></li>
          </>
        )}
        {userRole ? (
          <>
            <li><Link to="/profile">Profile</Link></li>
            <li>
              <button onClick={toggleCartDropdown}>Cart</button>
              {cartDropdownVisible && (
                <div className="cart-dropdown">
                  {cartItems.length === 0 ? (
                    <p>No items in cart</p>
                  ) : (
                    <ul className="cart-list">
                      {cartItems.map(item => (
                        <li key={item.item_id} className="cart-item">
                          <div className="cart-item-img">
                            {item.img ? <img src={item.img} alt={item.name} /> : <img src={defaultImage} alt={item.name} />}
                          </div>
                          <div className="cart-item-name">{item.name}</div>
                          <div className="cart-item-quantity">
                            Qty: {item.quantity}
                            <button onClick={() => updateCartItemQuantity(item.item_id, item.quantity + 1)}>+</button>
                            <button onClick={() => updateCartItemQuantity(item.item_id, item.quantity - 1)}>-</button>
                          </div>
                          <button onClick={() => deleteCartItem(item.item_id)}>Remove</button>
                        </li>
                      ))}
                    </ul>
                  )}
                  <button onClick={handleCheckout}>Checkout</button>
                </div>
              )}
            </li>
            <li><button onClick={handleSignOut}>Sign Out</button></li>
          </>
        ) : (
          <li><Link to="/signin">Sign In</Link></li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
