import React, { useState, useEffect, useRef } from 'react';
import VendorInfo from './vendordetailscomponents/VendorInfo';
import TablesSection from './TablesSection';
import ItemsSection from './ItemsSection';
import AdminSection from './vendordetailscomponents/AdminSection';
import Pagination from './Pagination';
import ErrorMessage from './vendordetailscomponents/ErrorMessage';

const VendorDetails = ({
  vendor,
  userRole,
  tables,
  items,
  admins,
  error,
  currentPage,
  totalPages,
  fetchVendorTables,
  fetchVendorItems,
  handleAddTable,
  handleRemoveTable,
  handleEditTable,
  handleAddItem,
  handleRemoveItem,
  handleEditItem,
  handleAddAdmin,
  handleRemoveAdmin,
  handlePreviousPage,
  handleNextPage,
  // Add more props as needed
}) => {
  const [isEditingTable, setIsEditingTable] = useState(false);
  const [isEditingItem, setIsEditingItem] = useState(false);
  const [newTableName, setNewTableName] = useState('');
  const [newItemName, setNewItemName] = useState('');
  const [newItemPrice, setNewItemPrice] = useState('');
  const [editTableName, setEditTableName] = useState('');
  const [editItemName, setEditItemName] = useState('');
  const [editItemPrice, setEditItemPrice] = useState('');
  const [preview, setPreview] = useState(null);
  const imageRef = useRef(null);

  // Other necessary state and logic here...

  return (
    <div className="page-container">
      <VendorInfo vendor={vendor} />
      <TablesSection
        tables={tables}
        userRole={userRole}
        error={error}
        setIsEditingTable={setIsEditingTable}
        setEditTableName={setEditTableName}
        newTableName={newTableName}
        setNewTableName={setNewTableName}
        handleAddTable={handleAddTable}
        handleRemoveTable={handleRemoveTable}
        handleEditTable={handleEditTable}
        fetchVendorTables={fetchVendorTables}
      />
      <ItemsSection
        items={items}
        userRole={userRole}
        error={error}
        setIsEditingItem={setIsEditingItem}
        setEditItemName={setEditItemName}
        setEditItemPrice={setEditItemPrice}
        newItemName={newItemName}
        setNewItemName={setNewItemName}
        newItemPrice={newItemPrice}
        setNewItemPrice={setNewItemPrice}
        handleAddItem={handleAddItem}
        handleRemoveItem={handleRemoveItem}
        handleEditItem={handleEditItem}
        preview={preview}
        setPreview={setPreview}
        imageRef={imageRef}
      />
      <AdminSection
        admins={admins}
        userRole={userRole}
        error={error}
        handleAddAdmin={handleAddAdmin}
        handleRemoveAdmin={handleRemoveAdmin}
      />
      <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        handlePreviousPage={handlePreviousPage}
        handleNextPage={handleNextPage}
      />
      <ErrorMessage error={error} />
    </div>
  );
};

export default VendorDetails;
