import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [vendor, setVendor] = useState(null);
    const [admins, setAdmins] = useState([]);
    const [tables, setTables] = useState([]);
    const [items, setItems] = useState([]);
    const [newAdminEmail, setNewAdminEmail] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const imageRef = useRef(null);
    const [errorMessage, setErrorMessage] = useState(null);
    const [currentUserId, setCurrentUserId] = useState(null); // Add this line

    const handleErrorMessageTimeout = () => {
        setErrorMessage(null);
        setError(null); // Clear the error state as well
    };

    const fetchVendorItems = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/items`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to fetch vendor items';
                setError(errorMessage);
                return;
            }

            const data = await response.json();
            console.log("Fetched items:", data.items); // Log the items
            setItems(data.items || []);
        } catch (error) {
            console.error("Error fetching items:", error); // Log any errors
            setError('An unexpected error occurred while fetching vendor items');
        }
    }, [id]);

    const fetchVendorDetails = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'An error occurred while fetching vendor details';
                setError(errorMessage);
                return;
            }

            const data = await response.json();
            if (data && data.vendor) {
                setVendor(data.vendor);
            } else {
                setError('Vendor data is undefined or null');
            }
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor details');
        } finally {
            setLoading(false);
        }
    }, [id]);

    const fetchUserRole = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (token) {
                const response = await fetch('http://localhost:8080/me', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    const errorMessage = errorData.error || 'Failed to fetch user role';
                    setError(errorMessage);
                    return;
                }

                const data = await response.json();
                setUserRole(data.me.user_role);
                setCurrentUserId(data.me.user_info.id); // Update the currentUserId state correctly
            }
        } catch (error) {
            setError('An unexpected error occurred while fetching user role');
        }
    }, []);

    const fetchVendorAdmins = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to fetch vendor admins';
                setError(errorMessage);
                return;
            }

            const data = await response.json();
            setAdmins(data.vendor_admin || []);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor admins');
        }
    }, [id]);

    const fetchVendorTables = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to fetch vendor tables';
                setError(errorMessage);
                return;
            }

            const data = await response.json();
            setTables(data.tables || []);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor tables');
        }
    }, [id]);

    const fetchUserTables = useCallback(async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await fetch("http://localhost:8080/usertable", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error;
                if (errorMessage === "User already have a table") {
                    setErrorMessage("You already have a table!"); // Updated message
                } else if (errorMessage !== "User has no table") {
                    setError(errorMessage);
                }
                return []; // Return an empty array on error
            }

            const data = await response.json();
            return Array.isArray(data.tables) ? data.tables : []; // Ensure this always returns an array

        } catch (error) {
            setErrorMessage('An unexpected error occurred while fetching user tables');
        }
    }, []);

    useEffect(() => {
        if (errorMessage) {
            const timeoutId = setTimeout(handleErrorMessageTimeout, 3000); // 3000ms = 3 seconds
            return () => clearTimeout(timeoutId);
        }

        if (error) {
            const timeoutId = setTimeout(handleErrorMessageTimeout, 3000); // 3000ms = 3 seconds
            return () => clearTimeout(timeoutId);
        }
    }, [error, errorMessage]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                await fetchVendorDetails();
                await fetchUserRole();
                await fetchVendorAdmins();
                await fetchVendorTables();
                await fetchVendorItems(); // Fetch vendor items
                await fetchUserTables(); // Add this line to call the function
            } catch (error) {
                console.error("Error fetching data:", error); // Log the error
                setError('An error occurred while fetching data.'); // Set error state if needed
            }
        };
        fetchData();
    }, [id, fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems, fetchUserTables]);

    const handleImageHover = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'pointer';
        }
    };

    const handleImageLeave = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'default';
        }
    };

    const handleImageError = (e) => {
        e.target.src = defaultimage;
    };

    const handleAddAdmin = async () => {
        if (!newAdminEmail) return;

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    Authorization: `Bearer ${token}`,
                },
                body: new URLSearchParams({ Email: newAdminEmail }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to add admin';
                setError(errorMessage);
                return;
            }

            setNewAdminEmail('');
            fetchVendorAdmins(); // Refresh admin list
        } catch (error) {
            setError('An unexpected error occurred while adding admin');
        }
    };

    const handleRemoveAdmin = async (adminId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to remove admin';
                setError(errorMessage);
                return;
            }

            // Remove the admin from the list
            setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
        } catch (error) {
            setError('An unexpected error occurred while removing admin');
        }
    };

    const handleRemoveTable = async (tableId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to remove table';
                setError(errorMessage);
                return;
            }

            setTables((prevTables) => prevTables.filter((table) => table.table_id !== tableId));
        } catch (error) {
            setError('An unexpected error occurred while removing table');
        }
    };

    const handleAddTable = async () => {
        try {
            const userTables = await fetchUserTables();
            if (userTables && userTables.length > 0) {
                setErrorMessage('You already have a table!');
                return;
            }

            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to add table';
                setError(errorMessage);
                return;
            }

            const newTable = await response.json();
            setTables((prevTables) => [...prevTables, newTable.table]);
        } catch (error) {
            setError('An unexpected error occurred while adding table');
        }
    };

    const handleBookTable = async (tableId) => {
        try {
            const userTables = await fetchUserTables();
            if (userTables && userTables.length > 0) {
                setErrorMessage('You already have a table!');
                return;
            }

            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}/book`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to book table';
                setError(errorMessage);
                return;
            }

            // Update the table status to booked
            setTables((prevTables) =>
                prevTables.map((table) =>
                    table.table_id === tableId ? { ...table, status: 'booked' } : table
                )
            );
        } catch (error) {
            setError('An unexpected error occurred while booking table');
        }
    };

    const handleUnbookTable = async (tableId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}/unbook`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to unbook table';
                setError(errorMessage);
                return;
            }

            // Update the table status to available
            setTables((prevTables) =>
                prevTables.map((table) =>
                    table.table_id === tableId ? { ...table, status: 'available' } : table
                )
            );
        } catch (error) {
            setError('An unexpected error occurred while unbooking table');
        }
    };

    const handleAddItem = async () => {
        navigate(`/vendor/${id}/items/new`);
    };

    const handleUpdateItem = async (itemId) => {
        navigate(`/vendor/${id}/items/${itemId}/edit`);
    };

    const handleDeleteItem = async (itemId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/items/${itemId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to delete item';
                setError(errorMessage);
                return;
            }

            setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
        } catch (error) {
            setError('An unexpected error occurred while deleting item');
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    if (error) {
        return <div className="error">{error}</div>;
    }

    return (
        <div className="vendor-details-container">
            <div className="vendor-info">
                <img
                    ref={imageRef}
                    src={vendor?.image_url || defaultimage}
                    alt="Vendor"
                    className="vendor-image"
                    onError={handleImageError}
                    onMouseEnter={handleImageHover}
                    onMouseLeave={handleImageLeave}
                />
                <div className="vendor-details">
                    <h2 className="vendor-name">{vendor?.name}</h2>
                    <p className="vendor-description">{vendor?.description}</p>
                </div>
            </div>

            {userRole === '1' && (
                <div className="admin-section">
                    <h3>Manage Admins</h3>
                    <ul className="admin-list">
                        {admins.map((admin) => (
                            <li key={admin.user_id}>
                                {admin.email}
                                {admin.user_id !== currentUserId && ( // Hide remove button for the current user
                                    <button onClick={() => handleRemoveAdmin(admin.user_id)} className="remove-admin-button">Remove</button>
                                )}
                            </li>
                        ))}
                    </ul>
                    <input
                        type="email"
                        value={newAdminEmail}
                        onChange={(e) => setNewAdminEmail(e.target.value)}
                        placeholder="Enter new admin email"
                    />
                    <button onClick={handleAddAdmin} className="add-admin-button">Add Admin</button>
                </div>
            )}

            <div className="table-section">
                <h3>Manage Tables</h3>
                <ul className="table-list">
                    {tables.map((table) => (
                        <li key={table.table_id}>
                            <span>Table {table.table_id}</span>
                            <span>Status: {table.status}</span>
                            {userRole === '1' && (
                                <>
                                    <button onClick={() => handleRemoveTable(table.table_id)} className="remove-table-button">Remove Table</button>
                                    {table.status === 'available' ? (
                                        <button onClick={() => handleBookTable(table.table_id)} className="book-table-button">Book Table</button>
                                    ) : (
                                        <button onClick={() => handleUnbookTable(table.table_id)} className="unbook-table-button">Unbook Table</button>
                                    )}
                                </>
                            )}
                        </li>
                    ))}
                </ul>
                {userRole === '1' && <button onClick={handleAddTable} className="add-table-button">Add Table</button>}
            </div>

            {userRole === '1' && (
                <div className="item-section">
                    <h3>Manage Items</h3>
                    <ul className="item-list">
                        {items.map((item) => (
                            <li key={item.id}>
                                <span>{item.name}</span>
                                <button onClick={() => handleUpdateItem(item.id)} className="update-item-button">Update</button>
                                <button onClick={() => handleDeleteItem(item.id)} className="delete-item-button">Delete</button>
                            </li>
                        ))}
                    </ul>
                    <button onClick={handleAddItem} className="add-item-button">Add Item</button>
                </div>
            )}

            {errorMessage && <div className="error-message">{errorMessage}</div>}
        </div>
    );
}

export default VendorDetails;
