import React, { useEffect, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Fixed import
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';
import defaultImage from '../css/vendor.jpg';
import { useOrderUpdate } from './OrderUpdateContext'; // Import the context

const Navbar = ({ initialCartItems, onCartItemsChange, refreshCart }) => {
    const [userRole, setUserRole] = useState(null);
    const [isTransparent, setIsTransparent] = useState(false);
    const [cartDropdownVisible, setCartDropdownVisible] = useState(false);
    const [cartItems, setCartItems] = useState(initialCartItems || []);
    const [editedCartItems, setEditedCartItems] = useState([]); // Track the edited cart items here
    const [cart, setCart] = useState({});
    const [cartDescription, setCartDescription] = useState('');
    const [initialCartDescription, setInitialCartDescription] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);
    const [successMessage, setSuccessMessage] = useState(null);
    const [totalPrice, setTotalPrice] = useState(0);
    const [totalQuantity, setTotalQuantity] = useState(0);
    const [loadingCartItems, setLoadingCartItems] = useState(false);
    const [loadingCheckout, setLoadingCheckout] = useState(false);
    const [isQuantitySaved, setIsQuantitySaved] = useState(true); // State to track if quantity and description are saved
    const navigate = useNavigate();
    const dropdownRef = useRef(null);
    const { setShouldUpdateOrders } = useOrderUpdate(); // Use the context
    const [isSaving, setIsSaving] = useState(false);

    useEffect(() => {
        fetchCartItems();
    }, [refreshCart]);

    // Check if the current state matches the initial state (to hide the save button)
    const checkIfValuesChanged = () => {
        const descriptionChanged = cartDescription !== initialCartDescription;
        const quantitiesChanged = editedCartItems.some((item, index) => item.quantity !== cartItems[index].quantity);

        return descriptionChanged || quantitiesChanged;
    };

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;
                if (decodedToken.exp < currentTime) {
                    localStorage.removeItem('token');
                    setUserRole(null);
                } else {
                    setUserRole(decodedToken.userRole);
                    fetchCartItems();
                }
            } catch (error) {
                console.error('Error decoding token:', error);
                localStorage.removeItem('token');
                setUserRole(null);
            }
        }
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            setIsTransparent(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setCartDropdownVisible(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const fetchCartItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) return;

        try {
            setLoadingCartItems(true);
            const [itemsResponse, cartResponse] = await Promise.all([
                fetch('http://localhost:8080/cartitems', {
                    headers: { Authorization: `Bearer ${token}` }
                }),
                fetch('http://localhost:8080/carts', {
                    headers: { Authorization: `Bearer ${token}` }
                })
            ]);

            if (!itemsResponse.ok || !cartResponse.ok) {
                throw new Error('You must have a table to checkout!');
            }

            const itemsData = await itemsResponse.json();
            setCartItems(itemsData.cart || []);
            setEditedCartItems(itemsData.cart || []); // Initialize editedCartItems with the current cart items

            const cartData = await cartResponse.json();
            setCart(cartData);
            setCartDescription(cartData.cart?.description || '');
            setInitialCartDescription(cartData.cart?.description || '');
            setTotalPrice(cartData.cart?.total_price || 0);
            updateTotalQuantityAndPrice(itemsData.cart); // Update total quantity and price based on items
            setIsQuantitySaved(true); // Reset quantity saved state

        } catch (error) {
            console.error('Error fetching cart items:', error);
            setErrorMessage('Failed to load cart items.');
        } finally {
            setLoadingCartItems(false);
        }
    };

    const handleSignOut = () => {
        localStorage.removeItem('token');
        setUserRole(null);
        navigate('/signin');
    };

    const toggleCartDropdown = () => {
        setCartDropdownVisible(prev => !prev);
    };

    const handleCartDescriptionChange = (e) => {
        const value = e.target.value;
        if (value.length <= 100) {
            setCartDescription(value);
            setIsQuantitySaved(false); // Mark the state as unsaved since the description changed
            setErrorMessage(null); // Clear error if it's within the limit
        } else {
            setErrorMessage('Description cannot exceed 100 characters.');
        }
    };

    const deleteCartItem = async (itemId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.errors ? JSON.stringify(errorData.errors) : 'Network response was not ok');
            }

            console.log('Item deleted successfully');
            await fetchCartItems();
        } catch (error) {
            console.error('Error deleting cart item:', error);
            setErrorMessage('Error removing item. Please try again.');
        }
    };

    const updateCartItemQuantityLocally = (itemId, newQuantity) => {
        if (newQuantity < 1) return;

        const updatedItems = editedCartItems.map(item =>
            item.item_id === itemId ? { ...item, quantity: newQuantity } : item
        );
        setEditedCartItems(updatedItems);
        if (updatedItems.find(item => item.item_id === itemId).quantity === cartItems.find(item => item.item_id === itemId).quantity) {
            setIsQuantitySaved(true); // Reset quantity saved state if the quantity is returned to its default value
        } else {
            setIsQuantitySaved(false); // Indicate that new changes are unsaved
        }
        updateTotalQuantityAndPrice(updatedItems); // Update the total quantity and price based on new items
    };

    const updateTotalQuantityAndPrice = (items) => {
        const total = items.reduce((acc, item) => acc + item.quantity, 0);
        const price = items.reduce((acc, item) => {
            const itemPrice = item.price || 0; // Ensure item.price is a valid number or default to 0
            console.log(item.price)
              return acc + item.quantity * parseFloat(itemPrice); // Ensure the price is parsed as a float
        }, 0);
        
        setTotalQuantity(total);
        setTotalPrice(price);
    };
    const saveCartChanges = async () => {
        setLoadingCartItems(true);
        setIsSaving(true);
        try {
            const token = localStorage.getItem('token');

            // Update item quantities in the backend
            const quantityPromises = editedCartItems.map(item =>
                fetch(`http://localhost:8080/cartitems/${item.item_id}`, {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/x-www-form-urlencoded',
                        'Authorization': `Bearer ${token}`
                    },
                    body: new URLSearchParams({
                        item_id: item.item_id,
                        quantity: item.quantity
                    }).toString()
                })
            );

            // Update the cart description in the backend
            const descriptionPromise = fetch(`http://localhost:8080/carts/${cart.cart.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                },
                body: new URLSearchParams({
                    description: cartDescription
                }).toString()
            });

            // Wait for all promises to resolve
            const responses = await Promise.all([...quantityPromises, descriptionPromise]);

            // Check for any errors in the responses
            for (const response of responses) {
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.errors ? JSON.stringify(errorData.errors) : 'Error while saving cart changes');
                }
            }

            // Fetch updated cart after saving
            await fetchCartItems();

            setIsQuantitySaved(true); // Mark as saved
            setSuccessMessage('Cart updated successfully!'); // Show success message

            // Set a timer to clear the success message after 30 seconds
            setTimeout(() => {
                setSuccessMessage(null);
            }, 30000);
        } catch (error) {
            console.error('Error saving cart changes:', error);
            setErrorMessage(error.message || 'An unknown error occurred');
        } finally {
            setLoadingCheckout(false);
            setIsSaving(false);
        }
    };

    const handleCheckout = async () => {
        if (!isQuantitySaved) {
            setErrorMessage('Please save your changes before checking out.');
            return;
        }

        setLoadingCheckout(true);
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/checkout', {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.errors ? JSON.stringify(errorData.errors) : 'Network response was not ok');
            }

            console.log('Checkout successful');
            setCartItems([]);
            setShouldUpdateOrders(true); // Notify context that orders need to be updated
            window.location.reload(); // Refresh the page
        } catch (error) {
            console.error('Error during checkout:', error);
            setErrorMessage(error.message);
        } finally {
            setLoadingCheckout(false);
        }
    };

    return (
        <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="center-links">
                <li><Link to="/">Home</Link></li>
            </ul>
            <ul className="end-links">
                {userRole === "1" && (
                    <>
                        <li><Link to="/add-vendor">Add Vendor</Link></li>
                        <li><Link to="/users">Users</Link></li>
                    </>
                )}
                {userRole ? (
                    <>
                        <li><Link to="/profile">Profile</Link></li>
                        <li><Link to="/orders">Orders</Link></li>
                        <li>
                            <button onClick={toggleCartDropdown} disabled={loadingCartItems}>
                                {loadingCartItems ? <div className="spinner"></div> : 'Cart'}
                            </button>
                            {cartDropdownVisible && (
                                <div className="cart-dropdown" ref={dropdownRef}>
                                    {loadingCartItems ? (
                                        <p>Loading cart items...</p>
                                    ) : cartItems.length === 0 ? (
                                        <p>No items in cart</p>
                                    ) : (
                                        <>
                                            <ul className="cart-list">
                                                {editedCartItems.map(item => (
                                                    <li key={item.item_id} className="cart-item">
                                                        <div className="cart-item-img">
                                                            <img src={item.img || defaultImage} alt={item.name}
                                                                onError={(e) => e.target.src = defaultImage} />
                                                        </div>
                                                        <div className="cart-item-name">{item.name}</div>
                                                        <div className="cart-item-quantity">
                                                            <button onClick={() => updateCartItemQuantityLocally(item.item_id, item.quantity - 1)}>-</button>
                                                            <span>{item.quantity}</span>
                                                            <button onClick={() => updateCartItemQuantityLocally(item.item_id, item.quantity + 1)}>+</button>
                                                        </div>
                                                        <button onClick={() => deleteCartItem(item.item_id)} className='removebutton'>Remove</button>
                                                    </li>
                                                ))}
                                            </ul>
                                            <div className="cart-summary">
                                                <p>Total Quantity: {totalQuantity}</p>
                                                <p>Total Price: ${totalPrice.toFixed(2)}</p>
                                            </div>
                                        </>
                                    )}

                                    {totalQuantity > 0 && (
                                        <div className="cart-description">
                                            <textarea
                                                value={cartDescription}
                                                onChange={handleCartDescriptionChange}
                                                placeholder="Write a description for your cart..."
                                                rows="3"
                                                maxLength="100"
                                            />
                                            <p>{cartDescription.length}/100</p> {/* Character count */}
                                        </div>
                                    )}

                                    {/* Save button is now with the description */}
                                    {checkIfValuesChanged() && (
                                        <button onClick={saveCartChanges} disabled={isSaving}>
                                            {isSaving ? 'Saving...' : 'Save'}
                                        </button>
                                    )}

                                    {isQuantitySaved && (
                                        <button onClick={handleCheckout} disabled={loadingCheckout}>
                                            {loadingCheckout ? 'Processing Checkout...' : 'Checkout'}
                                        </button>
                                    )}
                                    {successMessage && (
                                        <p style={{ color: 'green' }}>{successMessage}</p>
                                    )}
                                    {errorMessage && (
                                        <p style={{ color: 'red' }}>{errorMessage}</p>
                                    )}
                                </div>
                            )}
                        </li>
                        <li><button onClick={handleSignOut}>Sign Out</button></li>
                    </>
                ) : (
                    <li><Link to="/signin">Sign In</Link></li>
                )}
            </ul>
        </nav>
    );
};

export default Navbar;
