import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams } from 'react-router-dom';
import '../css/vendordetails.css';
import { useOrderUpdate } from './OrderUpdateContext';
import VendorItem from './VendorItem';
import AdminList from './AdminList';
import SearchBar from './SearchBar';
import AddItemForm from './AddItemForm';
import EditItemForm from './EditItemForm';
import Pagination from './Pagination';

function VendorDetails({ onAddToCart }) {
  const { id } = useParams();
  const [vendor, setVendor] = useState(null);
  const [items, setItems] = useState([]);
  const [tables, setTables] = useState([]);
  const [admins, setAdmins] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(12);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editItemId, setEditItemId] = useState(null);
  const { shouldUpdateOrders, setShouldUpdateOrders } = useOrderUpdate();
  const firstItemRef = useRef(null);

  const fetchVendorDetails = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.error || 'An error occurred while fetching vendor details');
        return;
      }

      const data = await response.json();
      setVendor(data.vendor);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor details');
    } finally {
      setLoading(false);
    }
  }, [id]);

  const fetchVendorItems = useCallback(async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`http://localhost:8080/vendor/${id}/items?page=${currentPage}&page_size=${itemsPerPage}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch items');
      }

      const data = await response.json();
      setItems(data.items || []);
    } catch (error) {
      console.error("Error fetching items:", error);
      setError('An unexpected error occurred while fetching vendor items');
    }
  }, [id, currentPage, itemsPerPage]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await fetchVendorDetails();
        await fetchVendorItems();
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('An error occurred while fetching data.');
        setLoading(false);
      }
    };
    fetchData();
  }, [fetchVendorDetails, fetchVendorItems]);

  const handleAddToCart = async (item) => {
    // Add item to cart logic here
  };

  if (loading) {
    return <div className="spinner"></div>;
  }

  return (
    <div className="vendor-details">
      <h1>{vendor?.name}</h1>

      {/* Search Bar */}
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />

      {/* Items */}
      <div className="items-list">
        {items.map(item => (
          <VendorItem
            key={item.id}
            item={item}
            onAddToCart={() => handleAddToCart(item)}
            onEdit={() => setEditItemId(item.id)}
            firstItemRef={firstItemRef}
          />
        ))}
      </div>

      {/* Pagination */}
      <Pagination
        currentPage={currentPage}
        totalItems={items.length}
        itemsPerPage={itemsPerPage}
        onPageChange={setCurrentPage}
      />

      {/* Admin List */}
      <AdminList admins={admins} fetchVendorAdmins={fetchVendorDetails} />

      {/* Add Item Form */}
      <AddItemForm fetchVendorItems={fetchVendorItems} />

      {/* Edit Item Form */}
      {editItemId && (
        <EditItemForm
          itemId={editItemId}
          fetchVendorItems={fetchVendorItems}
        />
      )}
    </div>
  );
}

export default VendorDetails;
