import React, { useEffect, useState } from 'react';
import { Routes, Route, BrowserRouter as Router, useLocation } from 'react-router-dom';
import Login from './components/login.js';
import Vendors from './components/vendors.js';
import VendorDetails from './components/vendordetails.js';
import AddVendor from './components/addvendor.js';
import Navbar from './components/navbar.js';
import Sidebar from './components/sidebar.js';
import Profile from './components/editprofile.js';
import UsersPage from './components/userspage.js';
import EditUser from './components/edituser.js';
import Editvendorer from './components/editvendor.js';
import AddToCartComponent from './components/AddToCartComponent'; // Import your add-to-cart component

function App() {
  const location = useLocation();
  const [cartItems, setCartItems] = useState([]);

  const noNavAndSidebarRoutes = ['/signin', '/signup'];

  const refreshCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8080/cartitems', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setCartItems(data.cart || []);
    } catch (error) {
      console.error('Error refreshing cart:', error);
    }
  };

  useEffect(() => {
    refreshCart(); // Fetch cart items initially
  }, []);

  return (
    <div>
      {!noNavAndSidebarRoutes.includes(location.pathname) && (
        <>
          <Navbar cartItems={cartItems} />
          <Sidebar />
        </>
      )}

      <div className="main-content">
        <Routes>
          <Route path="/signin" element={<Login />} />
          <Route path="/" element={<Vendors refreshCart={refreshCart} />} />
          <Route path="/vendors" element={<Vendors refreshCart={refreshCart} />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/vendor/:id" element={<VendorDetails refreshCart={refreshCart} />} />
          <Route path="/edit-vendor/:id" element={<Editvendorer />} />
          <Route path="/add-vendor" element={<AddVendor />} />
          <Route path="/users" element={<UsersPage />} />
          <Route path="/users/edit/:userId" element={<EditUser />} />
        </Routes>
      </div>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
