import React, {useContext , useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode';
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';
import defaultImage from '../css/vendor.jpg';
import { CartContext } from './cartconext';

const Navbar = () => {
    const [userRole, setUserRole] = useState(null);
    const [isTransparent, setIsTransparent] = useState(false);
    const [cartDropdownVisible, setCartDropdownVisible] = useState(false);
    const navigate = useNavigate();
    const { cartItems, setCartItems } = useContext(CartContext);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;
                if (decodedToken.exp < currentTime) {
                    localStorage.removeItem('token');
                    setUserRole(null);
                } else {
                    setUserRole(decodedToken.userRole);
                    fetchCartItems();
                }
            } catch (error) {
                console.error('Error decoding token:', error);
                localStorage.removeItem('token');
                setUserRole(null);
            }
        }
    }, []);
// In Navbar component
const refreshCart = async () => {
    try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:8080/cartitems', {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        });

        if (!response.ok) {
            throw new Error('Network response was not ok');
        }

        const data = await response.json();
        setCartItems(data.cart);
    } catch (error) {
        console.error('Error refreshing cart:', error);
    }
};

// Add a function call in the Navbar component useEffect
useEffect(() => {
    refreshCart();
}, []);

    useEffect(() => {
        const handleScroll = () => {
            setIsTransparent(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const fetchCartItems = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/cartitems', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            console.log('Cart items fetched:', data); // Log the response data

            setCartItems(data.cart || []); // Ensure cartItems is always an array
        } catch (error) {
            console.error('Error fetching cart items:', error);
        }
    };

    const handleSignOut = () => {
        console.log('Sign out button clicked');
        localStorage.removeItem('token');
        setUserRole(null);
        navigate('/signin');
    };

    const toggleCartDropdown = () => {
        console.log('Toggle cart dropdown');
        setCartDropdownVisible(!cartDropdownVisible);
    };

    const updateCartItemQuantity = async (itemId, newQuantity) => {
        if (newQuantity < 1) return; // Prevent quantity from going below 1

        console.log(`Updating cart item quantity: ${itemId}, ${newQuantity}`);
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                },
                body: new URLSearchParams({ item_id: itemId, quantity: newQuantity }) // Send new quantity
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error('Error response from server:', errorData);
                throw new Error('Network response was not ok');
            }

            fetchCartItems(); // Refresh cart items
        } catch (error) {
            console.error('Error updating cart item:', error);
        }
    };

    const deleteCartItem = async (itemId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                method: 'DELETE', // Use POST method to include a body
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`,
                },
                body: new URLSearchParams({ quantity: 0 }) // Include quantity in the body
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error('Error response from server:', errorData);
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            console.log('Item removed:', data);

            fetchCartItems(); // Refresh cart items
        } catch (error) {
            console.error('Error deleting cart item:', error);
        }
    };

    const handleCheckout = async () => {
        console.log('Checkout button clicked');
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/checkout', {
                method: 'POST',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Handle successful checkout
            console.log('Checkout successful');
            setCartItems([]); // Clear cart items after checkout
        } catch (error) {
            console.error('Error during checkout:', error);
        }
    };

    return (
        <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="center-links">
                <li><Link to="/">Home</Link></li>
            </ul>
            <ul className="end-links">
                {userRole === "1" && (
                    <>
                        <li><Link to="/add-vendor">Add Vendor</Link></li>
                        <li><Link to="/users">Users</Link></li>
                    </>
                )}
                {userRole ? (
                    <>
                        <li><Link to="/profile">Profile</Link></li>
                        <li>
                            <button onClick={toggleCartDropdown}>Cart</button>
                            {cartDropdownVisible && (
                                <div className="cart-dropdown">
      {cartItems.length === 0 ? (
        <p>No items in cart</p>
      ) :(
        <ul className="cart-list">
          {cartItems.map(item => (
                                                <li key={item.item_id} className="cart-item">
                                                    <div className="cart-item-img">
                                                        {item.img ? <img src={item.img} alt={item.name} /> : <div><img src={defaultImage} alt={item.name} /></div>}
                                                    </div>
                                                    <div className="cart-item-name">{item.name}</div>
                                                    <div className="cart-item-quantity">
                                                        Qty: {item.quantity}
                                                        <button onClick={() => updateCartItemQuantity(item.item_id, item.quantity + 1)}>+</button>
                                                        <button onClick={() => updateCartItemQuantity(item.item_id, item.quantity - 1)}>-</button>
                                                    </div>
                                                    <button onClick={() => deleteCartItem(item.id)}>Remove</button>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                    <button onClick={handleCheckout}>Checkout</button>
                                </div>
                            )}
                        </li>
                        <li><button onClick={handleSignOut}>Sign Out</button></li>
                    </>
                ) : (
                    <li><Link to="/signin">Sign In</Link></li>
                )}
            </ul>
        </nav>
    );
};

export default Navbar;
