import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [vendor, setVendor] = useState(null);
    const [admins, setAdmins] = useState([]);
    const [tables, setTables] = useState([]);
    const [items, setItems] = useState([]);
    const [newAdminEmail, setNewAdminEmail] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [currentUserId, setCurrentUserId] = useState(null);
    const imageRef = useRef(null);
    const [errorMessage, setErrorMessage] = useState(null);

    const handleErrorMessageTimeout = () => {
        setErrorMessage(null);
        setError(null);
    };

    const fetchVendorItems = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/items`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to fetch vendor items');
                return;
            }

            const data = await response.json();
            setItems(data.items || []);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor items');
        }
    }, [id]);

    const fetchVendorDetails = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'An error occurred while fetching vendor details');
                return;
            }

            const data = await response.json();
            setVendor(data.vendor || {});
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor details');
        } finally {
            setLoading(false);
        }
    }, [id]);

    const fetchUserRole = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (token) {
                const response = await fetch('http://localhost:8080/me', {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });

                if (!response.ok) {
                    const errorData = await response.json();
                    setError(errorData.error || 'Failed to fetch user role');
                    return;
                }

                const data = await response.json();
                setUserRole(data.me.user_role);
                setCurrentUserId(data.me.user_info.id);
            }
        } catch (error) {
            setError('An unexpected error occurred while fetching user role');
        }
    }, []);

    const fetchVendorAdmins = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to fetch vendor admins');
                return;
            }

            const data = await response.json();
            setAdmins(data.vendor_admin || []);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor admins');
        }
    }, [id]);

    const fetchVendorTables = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to fetch vendor tables');
                return;
            }

            const data = await response.json();
            setTables(data.tables || []);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor tables');
        }
    }, [id]);

    const fetchUserTables = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/usertable', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                if (errorData.error === 'User already have a table') {
                    setErrorMessage('You already have a table!');
                } else {
                    setError(errorData.error);
                }
                return [];
            }

            const data = await response.json();
            return Array.isArray(data.tables) ? data.tables : [];
        } catch (error) {
            setErrorMessage('An unexpected error occurred while fetching user tables');
        }
    }, []);

    useEffect(() => {
        if (errorMessage || error) {
            const timeoutId = setTimeout(handleErrorMessageTimeout, 3000);
            return () => clearTimeout(timeoutId);
        }
    }, [error, errorMessage]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                await fetchVendorDetails();
                await fetchUserRole();
                await fetchVendorAdmins();
                await fetchVendorTables();
                await fetchVendorItems();
                await fetchUserTables();
            } catch (error) {
                setError('An error occurred while fetching data.');
            }
        };
        fetchData();
    }, [fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems, fetchUserTables]);

    const handleImageHover = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'pointer';
        }
    };

    const handleImageLeave = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'default';
        }
    };

    const handleImageError = (e) => {
        e.target.src = defaultimage;
    };

    const handleAddAdmin = async () => {
        if (!newAdminEmail) return;

        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    Authorization: `Bearer ${token}`,
                },
                body: new URLSearchParams({ Email: newAdminEmail }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to add admin');
                return;
            }

            setNewAdminEmail('');
            fetchVendorAdmins();
        } catch (error) {
            setError('An unexpected error occurred while adding admin');
        }
    };

    const handleRemoveAdmin = async (adminId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to remove admin');
                return;
            }

            setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
        } catch (error) {
            setError('An unexpected error occurred while removing admin');
        }
    };

    const handleRemoveTable = async (tableId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                setError(errorData.error || 'Failed to remove table');
                return;
            }

            setTables((prevTables) => prevTables.filter((table) => table.id !== tableId));
        } catch (error) {
            setError('An unexpected error occurred while removing table');
        }
    };

    const handleEditTable = (tableId) => {
        navigate(`/vendor/${id}/tables/${tableId}/edit`);
    };

    const handleAddTable = () => {
        navigate(`/vendor/${id}/tables/new`);
    };

    const handleAddItem = () => {
        navigate(`/vendor/${id}/items/new`);
    };

    const handleEditItem = (itemId) => {
        navigate(`/vendor/${id}/items/${itemId}/edit`);
    };

    return (
        <div>
            <header>
                <h1>{vendor?.name || 'Vendor Details'}</h1>
                <img
                    src={vendor?.image || defaultimage}
                    alt="Vendor"
                    ref={imageRef}
                    onMouseOver={handleImageHover}
                    onMouseOut={handleImageLeave}
                    onError={handleImageError}
                    className="vendor-image"
                />
            </header>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <div>
                    <h2>Admins</h2>
                    <ul>
                        {admins.map((admin) => (
                            <li key={admin.user_id}>
                                {admin.email}
                                {userRole === 'admin' && (
                                    <button onClick={() => handleRemoveAdmin(admin.user_id)}>Remove</button>
                                )}
                            </li>
                        ))}
                    </ul>
                    {userRole === 'admin' && (
                        <div>
                            <input
                                type="text"
                                value={newAdminEmail}
                                onChange={(e) => setNewAdminEmail(e.target.value)}
                                placeholder="New admin email"
                            />
                            <button onClick={handleAddAdmin}>Add Admin</button>
                        </div>
                    )}
                    <h2>Tables</h2>
                    <button onClick={handleAddTable}>Add Table</button>
                    <ul>
                        {tables.map((table) => (
                            <li key={table.id}>
                                Table {table.id}
                                {userRole === 'admin' && (
                                    <>
                                        <button onClick={() => handleEditTable(table.id)}>Edit</button>
                                        <button onClick={() => handleRemoveTable(table.id)}>Remove</button>
                                    </>
                                )}
                            </li>
                        ))}
                    </ul>
                    <h2>Items</h2>
                    <button onClick={handleAddItem}>Add Item</button>
                    <ul>
                        {items.map((item) => (
                            <li key={item.id}>
                                {item.name}
                                {userRole === 'admin' && (
                                    <>
                                        <button onClick={() => handleEditItem(item.id)}>Edit</button>
                                    </>
                                )}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
            {error && <div className="error-message">{error}</div>}
            {errorMessage && <div className="error-message">{errorMessage}</div>}
        </div>
    );
}

export default VendorDetails;
