import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultImage from './vendor.jpg';

function VendorDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [tables, setTables] = useState([]);
  const [items, setItems] = useState([]);
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState(null);

  const fetchData = useCallback(async (url, setter) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(url, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        if (response.status === 401) {
          setError('Unauthorized access. Redirecting to sign-in page...');
          setTimeout(() => navigate('/signin'), 3000);
          return;
        }
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      console.log(`Fetched data from ${url}:`, data); // Log data for debugging
      setter(data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchVendorData = async () => {
      setLoading(true);
      await fetchData(`http://localhost:8080/vendors/${id}`, (data) => {
        setVendor(data.vendor);
        setAdmins(data.vendor_admin || []);
        setTables(data.tables || []);
        setItems(data.items || []);
      });
      await fetchData('http://localhost:8080/me', (data) => setUserRole(data.me.user_role));
      setLoading(false);
    };
    fetchVendorData();
  }, [id, fetchData]);

  const handleEditClick = () => navigate(`/edit-vendor/${id}`);
  const handleImageHover = () => (imageRef.current.style.cursor = 'pointer');
  const handleImageLeave = () => (imageRef.current.style.cursor = 'default');
  const handleImageError = (e) => (e.target.src = defaultImage);

  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });
      if (!response.ok) throw new Error('Failed to add admin');
      setNewAdminEmail('');
      await fetchData(`http://localhost:8080/vendors/${id}/admins`, setAdmins);
    } catch (error) {
      console.error('Error adding admin:', error);
      setError('Failed to add admin');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error-overlay"><div className="error-box">{error}</div></div>;
  if (!vendor) return <div>No vendor details available</div>;

  return (
    <div className="page-container">
      <div className="vendor-details-container">
        <div className="vendor-image-container">
          <img
            src={vendor.img || defaultImage}
            alt={vendor.name || 'Vendor'}
            className="vendor-image"
            ref={imageRef}
            onMouseOver={handleImageHover}
            onMouseOut={handleImageLeave}
            onError={handleImageError}
          />
        </div>
        <div className="vendor-info-container">
          <h1 className="vendor-name">{vendor.name}</h1>
          <p className="vendor-description">{vendor.description || 'No description available'}</p>
          {userRole === '1' && (
            <div className="admin-section">
              <h2>Admins</h2>
              <ul className="admins-list">
                {admins.length > 0 ? admins.map((admin) => (
                  <li key={admin.user_id}>
                    <span>Email: {admin.email.trim() ? admin.email : 'N/A'}</span>
                    <button className="remove-admin-button">Remove</button>
                  </li>
                )) : <li>No admins found</li>}
              </ul>
              <input
                type="email"
                value={newAdminEmail}
                onChange={(e) => setNewAdminEmail(e.target.value)}
                className="form-input"
                placeholder="Enter admin email"
              />
              <button className="add-admin-button" onClick={handleAddAdmin}>Add Admin</button>
            </div>
          )}
          {userRole === '1' && <button className="edit-button" onClick={handleEditClick}>Edit Vendor</button>}
        </div>
      </div>
      <div className="tables-section">
        <h2>Tables</h2>
        <ul className="tables-list">
          {tables.length > 0 ? tables.map((table) => (
            <li key={table.id}>
              <span>Table Name: {table.name}</span>
              <span>Status: {table.status}</span>
            </li>
          )) : <li>No tables found</li>}
        </ul>
      </div>
      <div className="items-section">
        <h2>Items</h2>
        <ul className="items-list">
          {items.length > 0 ? items.map((item) => (
            <li key={item.id}>
              <span>Item Name: {item.name}</span>
              <span>Price: {item.price}</span>
            </li>
          )) : <li>No items found</li>}
        </ul>
      </div>
    </div>
  );
}

export default VendorDetails;
