// src/components/Sidebar.js
import React, { useState, useEffect} from 'react';
import '../css/Sidebar.css'; // For styling


const Sidebar = () => {
  const [sidebarState, setSidebarState] = useState(0); // 0 = closed, 1 = opening, 2 = open, 3 = closing

  const toggleSidebar = () => {
    if (sidebarState === 0) {
      setSidebarState(1); // Start opening
    } else if (sidebarState === 2) {
      setSidebarState(3); // Start closing
    }
  };

  useEffect(() => {
    if (sidebarState === 1) {
      setTimeout(() => setSidebarState(2), 300); // Finish opening
    } else if (sidebarState === 3) {
      setTimeout(() => setSidebarState(0), 300); // Finish closing
    }
  }, [sidebarState]);

  return (
    <div>
      <div
        className={`sidebar ${sidebarState === 2 ? 'open' : ''}`}
        ref={sidebarRef}
      >
        {/* sidebar content */}
      </div>
      <button
        className="sidebar-toggle"
        onClick={toggleSidebar}
      >
        {/* toggle button content */}
      </button>
    </div>
  );
};

export default Sidebar;
