import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function EditTablePage() {
  const { id, tableId } = useParams();
  const navigate = useNavigate();
  const [tableNumber, setTableNumber] = useState('');
  const [status, setStatus] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTableDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:8080/vendors/${id}/tables/${tableId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          const errorMessage = errorData.error || 'Failed to fetch table details';
          setError(errorMessage);
          return;
        }

        const data = await response.json();
        setTableNumber(data.number);
        setStatus(data.status);
      } catch (error) {
        setError('An unexpected error occurred while fetching table details');
      }
    };

    fetchTableDetails();
  }, [id, tableId]);

  const handleEditTable = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ number: tableNumber, status }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to update table';
        setError(errorMessage);
        return;
      }

      navigate(`/vendor/${id}`);
    } catch (error) {
      setError('An unexpected error occurred while updating table');
    }
  };

  return (
    <div className="edit-table-page">
      <h2>Edit Table</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleEditTable}>
        <label>
          Table Number:
          <input
            type="text"
            value={tableNumber}
            onChange={(e) => setTableNumber(e.target.value)}
            required
          />
        </label>
        <label>
          Status:
          <input
            type="text"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          />
        </label>
        <button type="submit">Update Table</button>
      </form>
    </div>
  );
}

export default EditTablePage;
