import React, { useEffect, useState } from 'react';
import {jwtDecode} from 'jwt-decode'; // Import jwt-decode for decoding JWT tokens
import '../css/orders.css'; // Import the updated CSS

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const [isVendorOwner, setIsVendorOwner] = useState(false);
    const [vendorId, setVendorId] = useState(null);

    useEffect(() => {
        const fetchOrders = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('No token found. Please log in.');
                return;
            }

            try {
                // Decode the token to get the userId
                let decodedToken;
                try {
                    decodedToken = jwtDecode(token);
                } catch (decodeError) {
                    setError('Error decoding token.');
                    console.error('Error decoding token:', decodeError);
                    return;
                }

                const userId = decodedToken.userId;

                if (!userId) {
                    setError('Invalid token. User ID is missing.');
                    return;
                }

                // Fetch the vendor ID for the user
                const userResponse = await fetch(`http://localhost:8080/uservendors/${userId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!userResponse.ok) {
                    throw new Error('Network response was not ok');
                }
                console.log(decodedToken);

                const userData = await userResponse.json();
                const vendorId = userData.vendorId;
                setVendorId(vendorId);
                setIsVendorOwner(!!vendorId);

                // Fetch orders based on vendor ID or general orders
                const url = isVendorOwner ? `http://localhost:8080/vendororders/${vendorId}` : 'http://localhost:8080/orders';
                const response = await fetch(url, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setOrders(data.orders || []);
            } catch (error) {
                setError(`Error fetching orders: ${error.message}`);
                console.error('Error fetching orders:', error);
            }
        };

        fetchOrders();
    }, []);

    const updateOrderStatus = async (orderId, status) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/orderscompleted/${orderId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ status })
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Refresh the orders after status update
            const updatedOrders = orders.map(order => 
                order.id === orderId ? { ...order, status } : order
            );
            setOrders(updatedOrders);
        } catch (error) {
            setError(`Error updating order status: ${error.message}`);
            console.error('Error updating order status:', error);
        }
    };

    return (
        <div className="orders-container">
            <div className="orders-card">
                <div className="orders-header">Orders</div>
                {error && <p className="error-message">{error}</p>}
                <div className="orders-list">
                    {orders.map(order => (
                        <div key={order.id} className="order-item">
                            <div className="order-details">
                                <strong>Total Cost:</strong> ${order.total_order_cost}
                            </div>
                            <div className="order-details">
                                <strong>Status:</strong> {order.status}
                            </div>
                            <div className="order-vendor">
                                <strong>Vendor Name:</strong> {order.vendor_name}
                            </div>
                            <div className="order-items">
                                <strong>Items:</strong>
                                {order.item_names.map((item, index) => (
                                    <div key={index} className="order-item-details">
                                        <span>{item}</span>
                                        <span>${order.item_prices[index]}</span>
                                    </div>
                                ))}
                            </div>
                            {isVendorOwner && (
                                <div className="order-actions">
                                    <button onClick={() => updateOrderStatus(order.id, 'completed')} disabled={order.status === 'completed'}>
                                        Mark as Completed
                                    </button>
                                    <button onClick={() => updateOrderStatus(order.id, 'preparing')} disabled={order.status === 'preparing'}>
                                        Mark as Preparing
                                    </button>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Orders;
