import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';

const Navbar = () => {
    const [userRole, setUserRole] = useState(null);
    const [isTransparent, setIsTransparent] = useState(false);
    const [cartItems, setCartItems] = useState([]);
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;
                if (decodedToken.exp < currentTime) {
                    localStorage.removeItem('token');
                    setUserRole(null);
                } else {
                    setUserRole(decodedToken.userRole);
                }
            } catch (error) {
                localStorage.removeItem('token');
                setUserRole(null);
            }
        }
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            setIsTransparent(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    useEffect(() => {
        fetchCartItems();
    }, []);

    const fetchCartItems = () => {
        const token = localStorage.getItem('token');
        if (token) {
            fetch('http://localhost:8080/cartitems', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            })
                .then(response => response.json())
                .then(data => setCartItems(data.items)) // Adjust according to your API response
                .catch(error => console.error(error));
        }
    };

    const handleCartClick = () => {
        setDropdownOpen(!dropdownOpen);
    };

    const handleRemoveItem = (itemId) => {
        // Implement API call to remove item from cart
        const token = localStorage.getItem('token');
        fetch(`http://localhost:8080/cartitems/${itemId}`, {
            method: 'DELETE',
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
            .then(response => response.json())
            .then(data => {
                setCartItems(cartItems.filter(item => item.id !== itemId));
            })
            .catch(error => console.error(error));
    };

    const handleSignOut = () => {
        localStorage.removeItem('token');
        setUserRole(null);
        navigate('/signin');
    };

    return (
        <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="center-links">
                <li><Link to="/">Home</Link></li>
            </ul>
            <ul className="end-links">
                {userRole === "1" && (
                    <>
                        <li><Link to="/add-vendor">Add Vendor</Link></li>
                        <li><Link to="/users">Users</Link></li>
                    </>
                )}
                {userRole ? (
                    <>
                        <li><Link to="/profile">Profile</Link></li>
                        <li onClick={handleCartClick}>
                            Cart ({cartItems.length})
                            {dropdownOpen && (
                                <div className="dropdown">
                                    {cartItems.length === 0 ? (
                                        <p>Your cart is empty.</p>
                                    ) : (
                                        <ul>
                                            {cartItems.map(item => (
                                                <li key={item.id}>
                                                    <span>{item.name} - {item.quantity}</span>
                                                    <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                    <p>
                                        <Link to="/checkout">Proceed to Checkout</Link>
                                    </p>
                                </div>
                            )}
                        </li>
                        <li><button onClick={handleSignOut}>Sign Out</button></li>
                    </>
                ) : (
                    <li><Link to="/signin">Sign In</Link></li>
                )}
            </ul>
        </nav>
    );
};

export default Navbar;
