import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {jwtDecode} from 'jwt-decode';
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';
import defaultImage from '../css/vendor.jpg';

const Navbar = () => {
    const [userRole, setUserRole] = useState(null);
    const [isTransparent, setIsTransparent] = useState(false);
    const [cartItems, setCartItems] = useState([]);
    const [cartDropdownVisible, setCartDropdownVisible] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;
                if (decodedToken.exp < currentTime) {
                    localStorage.removeItem('token');
                    setUserRole(null);
                } else {
                    setUserRole(decodedToken.userRole);
                    fetchCartItems();
                }
            } catch (error) {
                console.error('Error decoding token:', error);
                localStorage.removeItem('token');
                setUserRole(null);
            }
        }
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            setIsTransparent(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const fetchCartItems = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/cartitems', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            console.log('Cart items fetched:', data); // Log the response data

            setCartItems(data.cart);
        } catch (error) {
            console.error('Error fetching cart items:', error);
        }
    };

    const handleSignOut = () => {
        localStorage.removeItem('token');
        setUserRole(null);
        navigate('/signin');
    };

    const toggleCartDropdown = () => {
        setCartDropdownVisible(!cartDropdownVisible);
    };

    return (
        <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="center-links">
                <li><Link to="/">Home</Link></li>
            </ul>
            <ul className="end-links">
                {userRole === "1" && (
                    <>
                        <li><Link to="/add-vendor">Add Vendor</Link></li>
                        <li><Link to="/users">Users</Link></li>
                    </>
                )}
                {userRole ? (
                    <>
                        <li><Link to="/profile">Profile</Link></li>
                        <li>
                            <button onClick={toggleCartDropdown}>Cart</button>
                            {cartDropdownVisible && (
                                <div className="cart-dropdown">
                                    {cartItems.length === 0 ? (
                                        <p>No items in cart</p>
                                    ) : (
                                        <ul className="cart-list">
                                            {cartItems.map(item => (
                                                <li key={item.item_id} className="cart-item">
                                                    <div className="cart-item-img">
                                                        {item.img ? <img src={item.img} alt={item.name} /> : <img >{defaultImage}</img>}
                                                    </div>
                                                    <div className="cart-item-name">{item.name}</div>
                                                    <div className="cart-item-quantity">Qty: {item.quantity}</div>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </div>
                            )}
                        </li>
                        <li><button onClick={handleSignOut}>Sign Out</button></li>
                    </>
                ) : (
                    <li><Link to="/signin">Sign In</Link></li>
                )}
            </ul>
        </nav>
    );
};

export default Navbar;
