import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [tables, setTables] = useState([]);
  const [items, setItems] = useState([]);
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState(null);
  const imageRef = useRef(null);

  const [isEditingTable, setIsEditingTable] = useState(false);
  const [tableToEdit, setTableToEdit] = useState(null);
  const [newTableNumber, setNewTableNumber] = useState('');
  const [newTableStatus, setNewTableStatus] = useState('');

  const fetchVendorDetails = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'An error occurred while fetching vendor details';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      if (data && data.vendor) {
        setVendor(data.vendor);
      } else {
        setError('Vendor data is undefined or null');
      }
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor details');
    } finally {
      setLoading(false);
    }
  }, [id]);

  const fetchUserRole = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const response = await fetch('http://localhost:8080/me', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          const errorMessage = errorData.error || 'Failed to fetch user role';
          setError(errorMessage);
          return;
        }

        const data = await response.json();
        setUserRole(data.me.user_role);
      }
    } catch (error) {
      setError('An unexpected error occurred while fetching user role');
    }
  }, []);

  const fetchVendorAdmins = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor admins';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setAdmins(data.vendor_admin || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor admins');
    }
  }, [id]);

  const fetchVendorTables = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor tables';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setTables(data.tables || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor tables');
    }
  }, [id]);

  const fetchVendorItems = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/items`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor items';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setItems(data.items || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor items');
    }
  }, [id]);

  useEffect(() => {
    fetchVendorDetails();
    fetchUserRole();
    fetchVendorAdmins();
    fetchVendorTables();
    fetchVendorItems();
  }, [fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchVendorItems]);

  const handleEditClick = () => {
    // Redirect to edit vendor page, if needed
    navigate(`/vendors/${id}/edit`);
  };

  const handleImageHover = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'pointer';
    }
  };

  const handleImageLeave = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'default';
    }
  };

  const handleImageError = (e) => {
    e.target.src = defaultimage;
  };

  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add admin';
        setError(errorMessage);
        return;
      }

      setNewAdminEmail('');
      fetchVendorAdmins(); // Refresh admin list
    } catch (error) {
      setError('An unexpected error occurred while adding admin');
    }
  };

  const handleRemoveAdmin = async (adminId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove admin';
        setError(errorMessage);
        return;
      }

      // Remove the admin from the list
      setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
    } catch (error) {
      setError('An unexpected error occurred while removing admin');
    }
  };

  const handleAddTable = () => {
    setIsEditingTable(true);
    setTableToEdit(null);
    setNewTableNumber('');
    setNewTableStatus('');
  };

  const handleEditTable = (table) => {
    setIsEditingTable(true);
    setTableToEdit(table);
    setNewTableNumber(table.number);
    setNewTableStatus(table.status);
  };

  const handleDeleteTable = async (tableId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to delete table';
        setError(errorMessage);
        return;
      }

      // Remove the table from the list
      setTables((prevTables) => prevTables.filter((table) => table.id !== tableId));
    } catch (error) {
      setError('An unexpected error occurred while deleting table');
    }
  };

  const handleSaveTable = async () => {
    if (!newTableNumber || !newTableStatus) return;

    try {
      const token = localStorage.getItem('token');
      const method = tableToEdit ? 'PUT' : 'POST';
      const url = tableToEdit
        ? `http://localhost:8080/vendor/${id}/tables/${tableToEdit.id}`
        : `http://localhost:8080/vendor/${id}/tables`;
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({
          number: newTableNumber,
          status: newTableStatus,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to save table';
        setError(errorMessage);
        return;
      }

      setIsEditingTable(false);
      fetchVendorTables(); // Refresh table list
    } catch (error) {
      setError('An unexpected error occurred while saving table');
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="vendor-details-container">
      <h1 className="vendor-name">{vendor ? vendor.name : 'Vendor Details'}</h1>
      <div className="vendor-image-container">
        <img
          ref={imageRef}
          src={vendor && vendor.image ? vendor.image : defaultimage}
          alt="Vendor"
          onError={handleImageError}
          onMouseEnter={handleImageHover}
          onMouseLeave={handleImageLeave}
          className="vendor-image"
        />
        {userRole === 1 && (
          <button className="edit-vendor-button" onClick={handleEditClick}>
            Edit Vendor
          </button>
        )}
      </div>

      <h2>Admins</h2>
      <ul className="admin-list">
        {admins.map((admin) => (
          <li key={admin.user_id} className="admin-list-item">
            {admin.email}
            {(userRole === 1 || userRole === 2) && (
              <button
                className="remove-admin-button"
                onClick={() => handleRemoveAdmin(admin.user_id)}
              >
                Remove
              </button>
            )}
          </li>
        ))}
      </ul>

      {userRole === 1 && (
        <div className="add-admin-form">
          <input
            type="email"
            placeholder="Admin email"
            value={newAdminEmail}
            onChange={(e) => setNewAdminEmail(e.target.value)}
          />
          <button className="add-admin-button" onClick={handleAddAdmin}>
            Add Admin
          </button>
        </div>
      )}

      <h2>Tables</h2>
      <ul className="table-list">
        {tables.map((table) => (
          <li key={table.id} className="table-list-item">
            Table {table.number} - Status: {table.status}
            {(userRole === 1 || userRole === 2) && (
              <>
                <button
                  className="edit-table-button"
                  onClick={() => handleEditTable(table)}
                >
                  Edit
                </button>
                <button
                  className="delete-table-button"
                  onClick={() => handleDeleteTable(table.id)}
                >
                  Delete
                </button>
              </>
            )}
          </li>
        ))}
      </ul>

      {(userRole === 1 || userRole === 2) && (
        <button className="add-table-button" onClick={handleAddTable}>
          Add Table
        </button>
      )}

      {isEditingTable && (
        <div className="edit-table-form">
          <h3>{tableToEdit ? 'Edit Table' : 'Add Table'}</h3>
          <input
            type="text"
            placeholder="Table number"
            value={newTableNumber}
            onChange={(e) => setNewTableNumber(e.target.value)}
          />
          <input
            type="text"
            placeholder="Table status"
            value={newTableStatus}
            onChange={(e) => setNewTableStatus(e.target.value)}
          />
          <button className="save-table-button" onClick={handleSaveTable}>
            Save
          </button>
          <button
            className="cancel-edit-button"
            onClick={() => setIsEditingTable(false)}
          >
            Cancel
          </button>
        </div>
      )}

      <h2>Items</h2>
      <ul className="item-list">
        {items.map((item) => (
          <li key={item.id} className="item-list-item">
            {item.name} - ${item.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default VendorDetails;
