import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [vendor, setVendor] = useState(null);
    const [admins, setAdmins] = useState([]);
    const [tables, setTables] = useState([]);
    const [items, setItems] = useState([]);
    const [newAdminEmail, setNewAdminEmail] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [currentUserId, setCurrentUserId] = useState(null);
    const [userOccupiedTables, setUserOccupiedTables] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);
    const imageRef = useRef(null);

    const fetchWithToken = async (url, options = {}) => {
        const token = localStorage.getItem('token');
        const response = await fetch(url, {
            ...options,
            headers: {
                ...options.headers,
                Authorization: `Bearer ${token}`,
            },
        });

        if (!response.ok) {
            const errorData = await response.json();
            const errorMessage = errorData.error || 'An unexpected error occurred';
            setError(errorMessage);
            throw new Error(errorMessage);
        }
        return response.json();
    };

    const fetchVendorDetails = useCallback(async () => {
        try {
            const data = await fetchWithToken(`http://localhost:8080/vendors/${id}`);
            setVendor(data.vendor);
        } catch (error) {
            console.error('Error fetching vendor details:', error);
        } finally {
            setLoading(false);
        }
    }, [id]);

    const fetchUserRole = useCallback(async () => {
        try {
            const data = await fetchWithToken('http://localhost:8080/me');
            setUserRole(data.me.user_role);
            setCurrentUserId(data.me.user_info.id);
        } catch (error) {
            console.error('Error fetching user role:', error);
        }
    }, []);

    const fetchVendorAdmins = useCallback(async () => {
        try {
            const data = await fetchWithToken(`http://localhost:8080/vendors/${id}/admins`);
            setAdmins(data.vendor_admin || []);
        } catch (error) {
            console.error('Error fetching vendor admins:', error);
        }
    }, [id]);

    const fetchVendorTables = useCallback(async () => {
        try {
            const data = await fetchWithToken(`http://localhost:8080/vendor/${id}/tables`);
            setTables(data.tables || []);
        } catch (error) {
            console.error('Error fetching vendor tables:', error);
        }
    }, [id]);

    const fetchUserTables = useCallback(async () => {
        try {
            const data = await fetchWithToken('http://localhost:8080/usertable');
            return Array.isArray(data.tables) ? data.tables : [];
        } catch (error) {
            setErrorMessage('An unexpected error occurred while fetching user tables');
            setTimeout(() => setErrorMessage(null), 3000);
            return [];
        }
    }, []);

    const fetchVendorItems = useCallback(async () => {
        try {
            const data = await fetchWithToken(`http://localhost:8080/vendor/${id}/items`);
            setItems(data.items || []);
        } catch (error) {
            console.error('Error fetching vendor items:', error);
        }
    }, [id]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                await fetchVendorDetails();
                await fetchUserRole();
                await fetchVendorAdmins();
                await fetchVendorTables();
                const occupiedTables = await fetchUserTables();
                setUserOccupiedTables(occupiedTables);
                setTables(prevTables =>
                    prevTables.map(table =>
                        occupiedTables.includes(table.id)
                            ? { ...table, customer_id: currentUserId }
                            : table
                    )
                );
                await fetchVendorItems();
            } catch (error) {
                console.error('Error during data fetch:', error);
            }
        };
        fetchData();
    }, [fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchUserTables, fetchVendorItems, currentUserId]);

    useEffect(() => {
        if (errorMessage || error) {
            const timeoutId = setTimeout(() => {
                setErrorMessage(null);
                setError(null);
            }, 3000);
            return () => clearTimeout(timeoutId);
        }
    }, [error, errorMessage]);

    const handleImageHover = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'pointer';
        }
    };

    const handleImageLeave = () => {
        if (imageRef.current) {
            imageRef.current.style.cursor = 'default';
        }
    };

    const handleImageError = (e) => {
        e.target.src = defaultimage;
    };

    const handleAddAdmin = async () => {
        if (!newAdminEmail) return;

        try {
            await fetchWithToken(`http://localhost:8080/vendors/${id}/admins`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
                body: new URLSearchParams({ Email: newAdminEmail }),
            });
            setNewAdminEmail('');
            fetchVendorAdmins(); // Refresh admin list
        } catch (error) {
            console.error('Error adding admin:', error);
        }
    };

    const handleRemoveAdmin = async (adminId) => {
        try {
            await fetchWithToken(`http://localhost:8080/vendors/${id}/admins/${adminId}`, { method: 'DELETE' });
            setAdmins(prevAdmins => prevAdmins.filter(admin => admin.user_id !== adminId));
        } catch (error) {
            console.error('Error removing admin:', error);
        }
    };

    const handleRemoveTable = async (tableId) => {
        try {
            await fetchWithToken(`http://localhost:8080/vendor/${id}/tables/${tableId}`, { method: 'DELETE' });
            setTables(prevTables => prevTables.filter(table => table.id !== tableId));
        } catch (error) {
            console.error('Error removing table:', error);
        }
    };

    const handleEditTable = (tableId) => {
        navigate(`/vendor/${id}/tables/${tableId}/edit`);
    };

    const handleAddTable = () => {
        navigate(`/vendor/${id}/tables/new`);
    };

    const handleAddItem = () => {
        navigate(`/vendor/${id}/items/new`);
    };

    const handleOccupyTable = async (tableId) => {
        try {
            await fetchWithToken(`http://localhost:8080/vendor/${id}/tables/${tableId}/needs-service`, { method: 'PUT' });
            setTables(prevTables =>
                prevTables.map(table =>
                    table.id === tableId
                        ? { ...table, is_available: false, is_needs_service: true, customer_id: currentUserId }
                        : table
                )
            );
        } catch (error) {
            console.error('Error occupying table:', error);
        }
    };

    const handleFreeTable = async (tableId) => {
        const table = tables.find(t => t.id === tableId);
        if (!table || table.customer_id !== currentUserId) {
            setError('You can only free the table you have assigned.');
            return;
        }

        try {
            await fetchWithToken(`http://localhost:8080/vendor/${id}/tables/${tableId}/freetable`, { method: 'PUT' });
            setTables(prevTables =>
                prevTables.map(table =>
                    table.id === tableId
                        ? { ...table, is_available: true, is_needs_service: false, customer_id: null }
                        : table
                )
            );
        } catch (error) {
            console.error('Error freeing table:', error);
        }
    };

    const handleUpdateNeedsService = async (tableId, isNeedsService) => {
        try {
            await fetchWithToken(`http://localhost:8080/vendor/${id}/tables/${tableId}/needs-service`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
                body: new URLSearchParams({ needs_service: isNeedsService }),
            });
            setTables(prevTables =>
                prevTables.map(table =>
                    table.id === tableId ? { ...table, is_needs_service: isNeedsService } : table
                )
            );
        } catch (error) {
            console.error('Error updating needs service:', error);
        }
    };

    if (loading) return <p>Loading...</p>;

    return (
        <div className="vendor-details">
            <h1>Vendor Details</h1>
            {vendor && (
                <div className="vendor-info">
                    <img
                        ref={imageRef}
                        src={vendor.image_url || defaultimage}
                        alt={vendor.name}
                        onMouseEnter={handleImageHover}
                        onMouseLeave={handleImageLeave}
                        onError={handleImageError}
                        className="vendor-image"
                    />
                    <h2>{vendor.name}</h2>
                    <p>{vendor.description}</p>
                </div>
            )}

            {error && <p className="error-message">{error}</p>}
            {errorMessage && <p className="error-message">{errorMessage}</p>}

            <div className="actions">
                {userRole === 'admin' && (
                    <>
                        <button onClick={handleAddAdmin}>Add Admin</button>
                        <input
                            type="text"
                            value={newAdminEmail}
                            onChange={e => setNewAdminEmail(e.target.value)}
                            placeholder="New admin email"
                        />
                    </>
                )}
            </div>

            <div className="admins">
                <h2>Admins</h2>
                {admins.length > 0 ? (
                    <ul>
                        {admins.map(admin => (
                            <li key={admin.user_id}>
                                {admin.user_info.email}
                                {userRole === 'admin' && (
                                    <button onClick={() => handleRemoveAdmin(admin.user_id)}>Remove</button>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No admins found.</p>
                )}
            </div>

            <div className="tables">
                <h2>Tables</h2>
                <button onClick={handleAddTable}>Add Table</button>
                {tables.length > 0 ? (
                    <ul>
                        {tables.map(table => (
                            <li key={table.id}>
                                Table #{table.number}
                                {table.is_available ? (
                                    <button onClick={() => handleOccupyTable(table.id)}>Occupy</button>
                                ) : (
                                    <button onClick={() => handleFreeTable(table.id)}>Free</button>
                                )}
                                {table.is_needs_service ? (
                                    <button onClick={() => handleUpdateNeedsService(table.id, false)}>Mark as Fixed</button>
                                ) : (
                                    <button onClick={() => handleUpdateNeedsService(table.id, true)}>Mark as Needing Service</button>
                                )}
                                {userRole === 'admin' && (
                                    <>
                                        <button onClick={() => handleEditTable(table.id)}>Edit</button>
                                        <button onClick={() => handleRemoveTable(table.id)}>Remove</button>
                                    </>
                                )}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No tables found.</p>
                )}
            </div>

            <div className="items">
                <h2>Items</h2>
                <button onClick={handleAddItem}>Add Item</button>
                {items.length > 0 ? (
                    <ul>
                        {items.map(item => (
                            <li key={item.id}>
                                {item.name} - ${item.price}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No items found.</p>
                )}
            </div>
        </div>
    );
}

export default VendorDetails;
