import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import jwtDecode from 'jwt-decode';
import defaultimage from './images/vendor.webp';

function VendorDetails() {
    const { id } = useParams();
    const [vendor, setVendor] = useState(null);
    const [admins, setAdmins] = useState([]);
    const [tables, setTables] = useState([]);
    const [newAdminEmail, setNewAdminEmail] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();
    const imageRef = useRef();
    const [hovered, setHovered] = useState(false);

    // Function to decode JWT token and extract user role
    const getUserRole = () => {
        const token = localStorage.getItem('token');
        if (token) {
            const decodedToken = jwtDecode(token);
            return decodedToken.userRole;
        }
        return null;
    };

    const userRole = getUserRole();

    const fetchVendorDetails = async () => {
        try {
            const response = await fetch(`http://localhost:8080/vendors/${id}`);
            const data = await response.json();
            setVendor(data);
            setLoading(false);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor details');
            setLoading(false);
        }
    };

    const fetchVendorAdmins = async () => {
        try {
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`);
            const data = await response.json();
            setAdmins(data);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor admins');
        }
    };

    const fetchVendorTables = async () => {
        try {
            const response = await fetch(`http://localhost:8080/vendors/${id}/tables`);
            const data = await response.json();
            setTables(data);
        } catch (error) {
            setError('An unexpected error occurred while fetching vendor tables');
        }
    };

    useEffect(() => {
        fetchVendorDetails();
        fetchVendorAdmins();
        fetchVendorTables();
    }, [id]);

    const handleAddAdmin = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({ email: newAdminEmail }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to add admin';
                setError(errorMessage);
                return;
            }

            fetchVendorAdmins(); // Refresh admin list
        } catch (error) {
            setError('An unexpected error occurred while adding admin');
        }
    };

    const handleRemoveAdmin = async (adminId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to remove admin';
                setError(errorMessage);
                return;
            }

            fetchVendorAdmins(); // Refresh admin list
        } catch (error) {
            setError('An unexpected error occurred while removing admin');
        }
    };

    const handleTableStatusChange = async (tableId, newStatus) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/tables/${tableId}/status`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    Authorization: `Bearer ${token}`,
                },
                body: new URLSearchParams({ status: newStatus }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                const errorMessage = errorData.error || 'Failed to update table status';
                setError(errorMessage);
                return;
            }

            fetchVendorTables(); // Refresh table list
        } catch (error) {
            setError('An unexpected error occurred while updating table status');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/signin');
    };

    const handleImageError = () => {
        imageRef.current.src = defaultimage;
    };

    const handleImageHover = () => {
        setHovered(true);
    };

    const handleImageLeave = () => {
        setHovered(false);
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div className="error-message">{error}</div>;
    }

    return (
        <div className="vendor-details-container">
            <h1>Vendor Details</h1>
            {vendor ? (
                <div className="vendor-details-content">
                    <div className="vendor-image-container">
                        <img
                            ref={imageRef}
                            src={vendor.img_url || defaultimage}
                            alt={vendor.vendor_name}
                            onError={handleImageError}
                            onMouseEnter={handleImageHover}
                            onMouseLeave={handleImageLeave}
                            className={hovered ? 'hovered' : ''}
                        />
                    </div>
                    <div className="vendor-info">
                        <h2>{vendor.vendor_name}</h2>
                        <p>Phone: {vendor.phone}</p>
                        <p>Email: {vendor.email}</p>
                        <p>Website: <a href={vendor.website}>{vendor.website}</a></p>
                        <p>Location: {vendor.location}</p>
                    </div>
                    {userRole === 'admin' && (
                        <div className="admin-section">
                            <h3>Admins</h3>
                            <ul>
                                {admins.map((admin) => (
                                    <li key={admin.id}>
                                        {admin.email}
                                        <button onClick={() => handleRemoveAdmin(admin.id)}>Remove</button>
                                    </li>
                                ))}
                            </ul>
                            <input
                                type="email"
                                value={newAdminEmail}
                                onChange={(e) => setNewAdminEmail(e.target.value)}
                                placeholder="Enter admin email"
                            />
                            <button onClick={handleAddAdmin}>Add Admin</button>
                        </div>
                    )}
                    <div className="tables-section">
                        <h3>Tables</h3>
                        <ul>
                            {tables.map((table) => (
                                <li key={table.id}>
                                    Table {table.table_number} - {table.status}
                                    {userRole === 'admin' && (
                                        <>
                                            <button onClick={() => handleTableStatusChange(table.id, 'occupied')}>Mark as Occupied</button>
                                            <button onClick={() => handleTableStatusChange(table.id, 'available')}>Mark as Available</button>
                                        </>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                    <button onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <div>No vendor details available.</div>
            )}
        </div>
    );
}

export default VendorDetails;
