import React, { useEffect, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Correct import for jwt-decode
import '../css/Navbar.css';
import logo from '../css/vendor.jpg';
import defaultImage from '../css/vendor.jpg';
import { useOrderUpdate } from './OrderUpdateContext'; // Import the custom hook

const Navbar = ({ initialCartItems, onCartItemsChange, refreshCart }) => {
    const [userRole, setUserRole] = useState(null);
    const [isTransparent, setIsTransparent] = useState(false);
    const [cartDropdownVisible, setCartDropdownVisible] = useState(false);
    const [cartItems, setCartItems] = useState(initialCartItems || []);
    const [cart, setCart] = useState(initialCartItems || []);
    const [cartDescription, setCartDescription] = useState(''); // Initialize with an empty string
    const [errorMessage, setErrorMessage] = useState(null);
    const [removalErrorMessage, setRemovalErrorMessage] = useState(null);
    const [totalPrice, setTotalPrice] = useState(0);
    const [totalQuantity, setTotalQuantity] = useState(0);
    const [loadingCartItems, setLoadingCartItems] = useState(false);
    const [loadingCheckout, setLoadingCheckout] = useState(false);
    const navigate = useNavigate();
    const dropdownRef = useRef(null);
    const { setShouldUpdateOrders } = useOrderUpdate(); // Use the context

    useEffect(() => {
        fetchCartItems();
    }, [refreshCart]);

    useEffect(() => {
        setLoadingCartItems(true);

        const token = localStorage.getItem('token');
        if (token) {
            try {
                const decodedToken = jwtDecode(token);
                const currentTime = Date.now() / 1000;
                if (decodedToken.exp < currentTime) {
                    localStorage.removeItem('token');
                    setUserRole(null);
                } else {
                    setUserRole(decodedToken.userRole);
                    fetchCartItems();
                }
            } catch (error) {
                console.error('Error decoding token:', error);
                localStorage.removeItem('token');
                setUserRole(null);
            }
        }
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            setIsTransparent(window.scrollY > 50);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setCartDropdownVisible(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const fetchCartItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) return;

        try {
            const [itemsResponse, cartResponse] = await Promise.all([
                fetch('http://localhost:8080/cartitems', {
                    headers: { 'Authorization': `Bearer ${token}` }
                }),
                fetch('http://localhost:8080/carts', {
                    headers: { 'Authorization': `Bearer ${token}` }
                })
            ]);

            if (!itemsResponse.ok || !cartResponse.ok) {
                throw new Error('You must have a table to checkout!');
            }

            const itemsData = await itemsResponse.json();
            setCartItems(itemsData.cart || []);
            console.log('Cart items:', itemsData.cart);

            const cartData = await cartResponse.json();
            setCart(cartData);

            console.log('Cart data:', cartData);

            setTotalPrice(cartData.cart?.total_price || 0);
            setTotalQuantity(cartData.cart?.quantity || 0);
            setCartDescription(cartData.cart?.description || ''); // Set the description from the cart data
            console.log('Fetched cart items:', itemsData.cart);

        } catch (error) {
            console.error('Error fetching cart items:', error);
        } finally {
            setLoadingCartItems(false);
        }
    };

    const deleteCart = async (id) => {
        try {
            const token = localStorage.getItem('token'); // Ensure token is retrieved correctly

            if (!token) {
                throw new Error('Token is missing');
            }

            const cartResponse = await fetch(`http://localhost:8080/carts/${id}`, {
                method: 'DELETE',
                headers: { 
                    'Authorization': `Bearer ${token}`,  // Ensure token is sent in the request header
                    'Content-Type': 'application/json'  // Add content type to headers
                }
            });

            if (!cartResponse.ok) {
                const errorData = await cartResponse.json();  // Parse the error response
                console.error('Error response from server:', errorData);  // Log detailed error
                throw new Error(`Failed to delete cart: ${errorData.error.role || 'Unknown error'}`);
            }

            console.log('Cart deleted successfully');
            setCartItems([]);  // Reset cart items after successful deletion
            setTotalPrice(0);   // Reset total price
            setTotalQuantity(0); // Reset total quantity

        } catch (error) {
            console.error('Error deleting cart:', error);
            setErrorMessage('Error deleting cart. Please try again.');
        } finally {
            setLoadingCartItems(false);
        }
    };

    const handleSignOut = () => {
        localStorage.removeItem('token');
        setUserRole(null);
        navigate('/signin');
    };

    const toggleCartDropdown = () => {
        setCartDropdownVisible(prev => !prev);
        setErrorMessage(null);
        setRemovalErrorMessage(null);
    };

    const updateCartItemQuantity = async (itemId, newQuantity) => {
        if (newQuantity < 1) return;

        setErrorMessage(null);
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                },
                body: new URLSearchParams({ item_id: itemId, quantity: newQuantity, description: cartDescription }).toString()
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            await fetchCartItems();
        } catch (error) {
            setErrorMessage('Error updating quantity. Please try again.');
            console.error('Error updating cart item:', error);
        } 
    };

    const deleteCartItem = async (itemId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:8080/cartitems/${itemId}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            console.log('Item deleted successfully');
            await fetchCartItems();
        } catch (error) {
            setRemovalErrorMessage('Error removing item. Please try again.');
            console.error('Error deleting cart item:', error);
        }
    };

    const handleCheckout = async () => {
        setLoadingCheckout(true);
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/checkout', {
                method: 'POST',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.errors ? JSON.stringify(errorData.errors) : 'Network response was not ok');
            }

            console.log('Checkout successful');
            setCartItems([]);
            setShouldUpdateOrders(true); // Notify context that orders need to be updated
            window.location.reload(); // Refresh the page
        } catch (error) {
            console.error('Error during checkout:', error);
            // Optionally, you could also set an error message in the state here
            setErrorMessage(error.message); // Assuming you have a state for error messages
        } finally {
            setLoadingCheckout(false);
        }
    };

    return (
        <nav className={`navbar ${isTransparent ? 'transparent' : ''}`}>
            <div className="logo">
                <img src={logo} alt="Logo" />
            </div>
            <ul className="center-links">
                <li><Link to="/">Home</Link></li>
            </ul>
            <ul className="end-links">
                {userRole === "1" && (
                    <>
                        <li><Link to="/add-vendor">Add Vendor</Link></li>
                        <li><Link to="/add-item">Add Item</Link></li>
                    </>
                )}
                <li><Link to="/about">About</Link></li>
                <li><Link to="/contact">Contact</Link></li>
                <li><Link to="/sign-in" onClick={handleSignOut}>Sign Out</Link></li>
                <li onClick={toggleCartDropdown} className="cart-icon">
                    <img src={defaultImage} alt="Cart" />
                    <span className="cart-quantity">{totalQuantity}</span>
                </li>
            </ul>

            {cartDropdownVisible && (
                <div className="cart-dropdown" ref={dropdownRef}>
                    <h2>Cart Items</h2>
                    {loadingCartItems ? (
                        <p>Loading...</p>
                    ) : (
                        <>
                            {cartItems.length > 0 ? (
                                <>
                                    {cartItems.map((item) => (
                                        <div key={item.id} className="cart-item">
                                            <img src={item.imageUrl} alt={item.name} />
                                            <div className="item-details">
                                                <h4>{item.name}</h4>
                                                <p>{item.description}</p>
                                                <p>Price: ${item.price}</p>
                                                <p>Quantity: {item.quantity}</p>
                                                <button onClick={() => updateCartItemQuantity(item.id, item.quantity + 1)}>+</button>
                                                <button onClick={() => updateCartItemQuantity(item.id, item.quantity - 1)}>-</button>
                                                <button onClick={() => deleteCartItem(item.id)}>Remove</button>
                                            </div>
                                        </div>
                                    ))}
                                    <div className="total-price">Total: ${totalPrice}</div>
                                    <textarea
                                        value={cartDescription}
                                        onChange={(e) => setCartDescription(e.target.value)}
                                        placeholder="Add description..."
                                    />
                                    <button onClick={handleCheckout} disabled={loadingCheckout}>
                                        {loadingCheckout ? 'Processing...' : 'Checkout'}
                                    </button>
                                </>
                            ) : (
                                <p>Your cart is empty.</p>
                            )}
                        </>
                    )}
                    {errorMessage && <p className="error-message">{errorMessage}</p>}
                    {removalErrorMessage && <p className="error-message">{removalErrorMessage}</p>}
                </div>
            )}
        </nav>
    );
};

export default Navbar;
