import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ cartItems, setCartItems }) => {
    const [cartDropdownVisible, setCartDropdownVisible] = useState(false);

    const toggleCartDropdown = () => {
        setCartDropdownVisible(!cartDropdownVisible);
    };

    const updateCartItemQuantity = (item_id, quantity) => {
        setCartItems(prevItems =>
            prevItems.map(item =>
                item.id === item_id ? { ...item, quantity } : item
            )
        );
    };

    const deleteCartItem = (item_id) => {
        setCartItems(prevItems =>
            prevItems.filter(item => item.id !== item_id)
        );
    };

    const handleCheckout = async () => {
        const token = localStorage.getItem('token');

        try {
            const response = await fetch('http://localhost:8080/checkout', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded',
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                console.error('Error response from server:', errorData);
                throw new Error('Network response was not ok');
            }

            alert('Checkout successful!');
            setCartItems([]);
        } catch (error) {
            console.error('Error during checkout:', error);
        }
    };

    return (
        <nav className="navbar">
            <div className="navbar-logo">
                <Link to="/">
                    <img src="/path/to/logo.png" alt="Logo" />
                </Link>
            </div>
            <div className="navbar-links">
                <Link to="/">Home</Link>
                <Link to="/add-vendor">Add Vendor</Link>
                <Link to="/profile">Profile</Link>
                <div className="cart-icon" onClick={toggleCartDropdown}>
                    Cart ({cartItems.length})
                    {cartDropdownVisible && (
                        <div className="cart-dropdown">
                            {cartItems.length > 0 ? (
                                <>
                                    {cartItems.map(item => (
                                        <div key={item.id} className="cart-item">
                                            <img src={item.img || '/path/to/defaultImage.png'} alt={item.name} />
                                            <div className="cart-item-info">
                                                <h4>{item.name}</h4>
                                                <p>Price: ${item.price}</p>
                                                <div className="quantity-controls">
                                                    <button onClick={() => updateCartItemQuantity(item.id, item.quantity - 1)}>-</button>
                                                    <span>{item.quantity}</span>
                                                    <button onClick={() => updateCartItemQuantity(item.id, item.quantity + 1)}>+</button>
                                                </div>
                                                <button className="remove-button" onClick={() => deleteCartItem(item.id)}>Remove</button>
                                            </div>
                                        </div>
                                    ))}
                                    <button className="checkout-button" onClick={handleCheckout}>Checkout</button>
                                </>
                            ) : (
                                <p>Your cart is empty.</p>
                            )}
                        </div>
                    )}
                </div>
                <button onClick={() => { /* handle sign out logic */ }}>Sign Out</button>
            </div>
        </nav>
    );
};

export default Navbar;
