import React, { useEffect, useState } from 'react';
import '../css/orders.css'; // Import the updated CSS
import {jwtDecode} from 'jwt-decode'; // Correct import for jwt-decode

const Orders = () => {
    const [orders, setOrders] = useState([]);
    const [error, setError] = useState(null);
    const [vendorId, setVendorId] = useState(null);

    useEffect(() => {
        const fetchVendorId = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) throw new Error('No token found');
                
                const decodedToken = jwtDecode(token);
                const userId = decodedToken.id; // Adjust if your token payload uses a different key
                
                const response = await fetch(`http://localhost:8080/uservendors/${userId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (!response.ok) throw new Error('Network response was not ok');
                
                const data = await response.json();
                setVendorId(data.vendorId); // Adjust based on your API response
            } catch (error) {
                setError('Error fetching vendor ID. Please try again.');
                console.error('Error fetching vendor ID:', error);
            }
        };

        const fetchOrders = async (vendorId) => {
            try {
                const response = await fetch(`http://localhost:8080/vendororders/${vendorId}`, {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (!response.ok) throw new Error('Network response was not ok');

                const data = await response.json();
                setOrders(data.orders || []);
            } catch (error) {
                setError('Error fetching orders. Please try again.');
                console.error('Error fetching orders:', error);
            }
        };

        // Fetch vendor ID first, then fetch orders
        fetchVendorId().then(() => {
            if (vendorId) fetchOrders(vendorId);
        });

    }, [vendorId]);
    return (
        <div className="orders-container">
            <div className="orders-card">
                <div className="orders-header">Orders</div>
                {error && <p className="error-message">{error}</p>}
                <div className="orders-list">
                    {orders.map(order => (
                        <div key={order.id} className="order-item">
                            <div className="order-details">
                                <strong>Total Cost:</strong> ${order.total_order_cost}
                            </div>
                            <div className="order-details">
                                <strong>Status:</strong> {order.status}
                            </div>
                            <div className="order-vendor">
                                <strong>Vendor Name:</strong> {order.vendor_name}
                            </div>
                            <div className="order-items">
                                <strong>Items:</strong>
                                {order.item_names.map((item, index) => (
                                    <div key={index} className="order-item-details">
                                        <span>{item}</span>
                                        <span>${order.item_prices[index]}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Orders;
