import React, { useState, useEffect } from 'react';

const VendorDetails = ({ id }) => {
  const [vendor, setVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newAdminEmail, setNewAdminEmail] = useState('');

  // Fetch data function
  const fetchData = async (url, setter) => {
    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Fetched data:', data);
      setter(data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to fetch data');
    }
  };

  // Fetch vendor details on component mount
  useEffect(() => {
    const fetchVendorData = async () => {
      setLoading(true);
      await fetchData(`http://localhost:8080/vendors/${id}`, setVendor);
      setLoading(false);
    };
    fetchVendorData();
  }, [id]);

  // Handle adding a new admin
  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;
    try {
      const token = localStorage.getItem('token');
      console.log('Adding new admin with email:', newAdminEmail);
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });
      if (!response.ok) throw new Error('Failed to add admin');
      console.log('Admin added successfully');
      setNewAdminEmail('');
      await fetchData(`http://localhost:8080/vendors/${id}`, setVendor); // Refresh vendor details
    } catch (error) {
      console.error('Error adding admin:', error);
      setError('Failed to add admin');
    }
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h2>Vendor Details</h2>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {vendor ? (
            <div>
              <p><strong>ID:</strong> {vendor.id}</p>
              <p><strong>Name:</strong> {vendor.name}</p>
              <p><strong>Email:</strong> {vendor.email}</p>
              {/* Add other vendor details as needed */}

              <h3>Admins</h3>
              {vendor.vendor_admin && vendor.vendor_admin.length > 0 ? (
                <ul>
                  {vendor.vendor_admin.map((admin) => (
                    <li key={admin.user_id}>
                      <span>Email: {admin.email}</span>
                      {/* Add functionality to remove admin if needed */}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>No admins found</p>
              )}

              <div>
                <input
                  type="email"
                  value={newAdminEmail}
                  onChange={(e) => setNewAdminEmail(e.target.value)}
                  placeholder="Enter new admin email"
                />
                <button onClick={handleAddAdmin}>Add Admin</button>
              </div>
            </div>
          ) : (
            <p>No vendor details available</p>
          )}
        </div>
      )}
    </div>
  );
};

export default VendorDetails;
