import React from 'react';
import '.../css/VendorDetails.css';

const ItemsSection = ({
  items,
  userRole,
  error,
  setIsEditingItem,
  setEditItemName,
  setEditItemPrice,
  newItemName,
  setNewItemName,
  newItemPrice,
  setNewItemPrice,
  handleAddItem,
  handleRemoveItem,
  handleEditItem,
  preview,
  setPreview,
  imageRef,
}) => {
  return (
    <div className="items-section card">
      <h2 className="section-title">Items</h2>
      {userRole === '1' && (
        <div className="add-buttons">
          <button onClick={() => setNewItemName('') || setNewItemPrice('') || setIsEditingItem(true)}>Add Item</button>
        </div>
      )}
      {/* Add your logic for displaying items and handling actions */}
      {items.map((item) => (
        <div key={item.id} className="item-card">
          <img src={item.img || 'defaultImage.png'} alt={item.name} className="item-image" />
          <h3 className="item-name">{item.name}</h3>
          <p className="item-price">Price: ${item.price.toFixed(2)}</p>
          <div className="edit-remove-row">
            <button onClick={() => handleEditItem(item)}>Edit</button>
            <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
          </div>
        </div>
      ))}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default ItemsSection;
