import React, { useState, useEffect, useCallback } from 'react';

function Vendors({ defaultImage }) {
  const [vendors, setVendors] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredVendors, setFilteredVendors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showDropdown, setShowDropdown] = useState(false);
  const [sortOrder, setSortOrder] = useState('latest');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const fetchVendors = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/vendors?page=${page}&sort=${sortOrder}`);
      const data = await response.json();
      setVendors(data.vendors);
      setTotalPages(data.totalPages);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch vendors');
      setLoading(false);
    }
  }, [page, sortOrder]);

  useEffect(() => {
    fetchVendors();
  }, [fetchVendors]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    if (value === '') {
      setShowDropdown(false);
      setFilteredVendors([]);
    } else {
      const filtered = vendors.filter((vendor) =>
        vendor.name.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredVendors(filtered);
      setShowDropdown(true);
    }
  };

  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
    setPage(1);
  };

  const handlePageChange = (pageNum) => {
    setPage(pageNum);
  };

  const handleSelectVendor = (vendorId) => {
    // Handle vendor selection from dropdown
  };

  const handleEditClick = (vendorId) => {
    // Handle edit button click
  };

  const handleAddVendorClick = () => {
    // Handle add vendor button click
  };

  const handleImageError = (e) => {
    e.target.src = defaultImage;
  };

  return (
    <div className="vendor-list">
      <h1 className="title">Vendor List</h1>
      {error && <p>{error}</p>}
      <div className="search-input-container">
        <select className="search-input" value={sortOrder} onChange={handleSortChange}>
          <option value="latest">Sort by Latest</option>
          <option value="oldest">Sort by Oldest</option>
          <option value="asc">Sort by Name (A-Z)</option>
          <option value="desc">Sort by Name (Z-A)</option>
        </select>
        <input
          type="text"
          className="search-input"
          placeholder="Search by name"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        {showDropdown && (
          <ul className="dropdown-menu">
            {filteredVendors.map((vendor) => (
              <li key={vendor.id} onClick={() => handleSelectVendor(vendor.id)}>
                {vendor.name}
              </li>
            ))}
          </ul>
        )}
      </div>
      {loading ? (
        <div className="spinner"></div>
      ) : (
        <div className="vendor-list-container">
          {vendors.map((vendor) => (
            <div
              key={vendor.id}
              className={`vendor ${!vendor.is_visible ? 'out-of-date' : ''}`}
              onClick={() => handleEditClick(vendor.id)}
            >
              <img src={vendor.image || defaultImage} alt={vendor.name} onError={handleImageError} />
              <h2>{vendor.name}</h2>
              <p>{vendor.description}</p>
              <button onClick={() => handleEditClick(vendor.id)}>Edit</button>
            </div>
          ))}
        </div>
      )}
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => i + 1).map((pageNum) => (
          <button key={pageNum} onClick={() => handlePageChange(pageNum)} className={page === pageNum ? 'active' : ''}>
            {pageNum}
          </button>
        ))}
      </div>
      <button onClick={handleAddVendorClick}>Add Vendor</button>
    </div>
  );
}

export default Vendors;
