import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/vendordetails.css';
import defaultimage from './vendor.jpg';

function VendorDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [admins, setAdmins] = useState([]);
  const [tables, setTables] = useState([]);
  const [items, setItems] = useState([]);
  const [newAdminEmail, setNewAdminEmail] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState(null);
  const [userOccupiedTables, setUserOccupiedTables] = useState([]);
  const imageRef = useRef(null);
  const [currentUserId, setCurrentUserId] = useState(null);

  const fetchVendorDetails = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'An error occurred while fetching vendor details';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      if (data && data.vendor) {
        setVendor(data.vendor);
      } else {
        setError('Vendor data is undefined or null');
      }
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor details');
    } finally {
      setLoading(false);
    }
  }, [id]);

  const fetchUserRole = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const response = await fetch('http://localhost:8080/me', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          const errorMessage = errorData.error || 'Failed to fetch user role';
          setError(errorMessage);
          return;
        }

        const data = await response.json();
        setUserRole(data.me.user_role);
        setCurrentUserId(data.me.id);
      }
    } catch (error) {
      setError('An unexpected error occurred while fetching user role');
    }
  }, []);

  const fetchVendorAdmins = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor admins';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setAdmins(data.vendor_admin || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor admins');
    }
  }, [id]);

  const fetchVendorTables = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor tables';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setTables(data.tables || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor tables');
    }
  }, [id]);

  const fetchVendorItems = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/items`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch vendor items';
        setError(errorMessage);
        return;
      }

      const data = await response.json();
      setItems(data.items || []);
    } catch (error) {
      setError('An unexpected error occurred while fetching vendor items');
    }
  }, [id]);

  const fetchUserTables = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8080/usertable', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to fetch user tables';
        setError(errorMessage);
        return [];
      }

      const data = await response.json();
      return data.tables || [];
    } catch (error) {
      setError('An unexpected error occurred while fetching user tables');
      return [];
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      await fetchVendorDetails();
      await fetchUserRole();
      await fetchVendorAdmins();
      await fetchVendorTables();

      const occupiedTables = await fetchUserTables();
      setUserOccupiedTables(occupiedTables);
    };

    fetchData();
  }, [fetchVendorDetails, fetchUserRole, fetchVendorAdmins, fetchVendorTables, fetchUserTables]);

  const handleImageHover = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'pointer';
    }
  };

  const handleImageLeave = () => {
    if (imageRef.current) {
      imageRef.current.style.cursor = 'default';
    }
  };

  const handleImageError = (e) => {
    e.target.src = defaultimage;
  };

  const handleAddAdmin = async () => {
    if (!newAdminEmail) return;

    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
          Authorization: `Bearer ${token}`,
        },
        body: new URLSearchParams({ Email: newAdminEmail }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add admin';
        setError(errorMessage);
        return;
      }

      setNewAdminEmail('');
      fetchVendorAdmins();
    } catch (error) {
      setError('An unexpected error occurred while adding admin');
    }
  };

  const handleRemoveAdmin = async (adminId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendors/${id}/admins/${adminId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove admin';
        setError(errorMessage);
        return;
      }

      setAdmins((prevAdmins) => prevAdmins.filter((admin) => admin.user_id !== adminId));
    } catch (error) {
      setError('An unexpected error occurred while removing admin');
    }
  };

  const handleRemoveTable = async (tableId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove table';
        setError(errorMessage);
        return;
      }

      setTables((prevTables) => prevTables.filter((table) => table.id !== tableId));
    } catch (error) {
      setError('An unexpected error occurred while removing table');
    }
  };

  const handleEditTable = (tableId) => {
    navigate(`/vendor/${id}/tables/${tableId}/edit`);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  const handleAddTable = () => {
    navigate(`/vendor/${id}/tables/new`);
  };

  const handleAddItem = () => {
    navigate(`/vendor/${id}/items/new`);
  };

  const handleOccupyTable = async (tableId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}/needs-service`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to occupy the table';
        setError(errorMessage);
        return;
      }

      setTables((prevTables) =>
        prevTables.map((table) => (table.id === tableId ? { ...table, status: 'needs-service' } : table))
      );
      setUserOccupiedTables((prevTables) => [...prevTables, tableId]);
    } catch (error) {
      setError('An unexpected error occurred while occupying the table');
    }
  };

  const handleFreeTable = async (tableId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables/${tableId}/free`, {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to free the table';
        setError(errorMessage);
        return;
      }

      setTables((prevTables) =>
        prevTables.map((table) => (table.id === tableId ? { ...table, status: 'available' } : table))
      );
      setUserOccupiedTables((prevTables) => prevTables.filter((occupiedTableId) => occupiedTableId !== tableId));
    } catch (error) {
      setError('An unexpected error occurred while freeing the table');
    }
  };

  const handleToggleTableStatus = (tableId, currentStatus) => {
    if (currentStatus === 'needs-service') {
      handleFreeTable(tableId);
    } else {
      handleOccupyTable(tableId);
    }
  };

  const handleRemoveItem = async (itemId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/items/${itemId}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to remove item';
        setError(errorMessage);
        return;
      }

      setItems((prevItems) => prevItems.filter((item) => item.id !== itemId));
    } catch (error) {
      setError('An unexpected error occurred while removing item');
    }
  };

  const handleEditItem = (itemId) => {
    navigate(`/vendor/${id}/items/${itemId}/edit`);
  };

  const handleEditVendor = () => {
    navigate(`/vendor/${id}/edit`);
  };

  const getStatusClassName = (status) => {
    if (status === 'available') {
      return 'available';
    } else if (status === 'needs-service') {
      return 'needs-service';
    } else {
      return '';
    }
  };

  if (!vendor) {
    return <div>Vendor not found</div>;
  }

  const handleNavigateTableDetails = (tableId) => {
    navigate(`/vendor/${id}/tables/${tableId}`);
  };

  return (
    <div className="vendor-details">
      <div className="vendor-header">
        <img
          src={`http://localhost:8080/${vendor.image}`}
          alt={vendor.name}
          className="vendor-image"
          ref={imageRef}
          onError={handleImageError}
          onMouseEnter={handleImageHover}
          onMouseLeave={handleImageLeave}
        />
        <div className="vendor-info">
          <h1>{vendor.name}</h1>
          <p>{vendor.description}</p>
          {userRole === 'admin' && (
            <div className="edit-button">
              <button onClick={handleEditVendor}>Edit Vendor</button>
            </div>
          )}
        </div>
      </div>
      <div className="vendor-section">
        <h2>Admins</h2>
        {error && <p className="error">{error}</p>}
        {userRole === 'admin' && (
          <div className="add-admin">
            <input
              type="email"
              value={newAdminEmail}
              onChange={(e) => setNewAdminEmail(e.target.value)}
              placeholder="Admin Email"
            />
            <button onClick={handleAddAdmin}>Add Admin</button>
          </div>
        )}
        <ul>
          {admins.map((admin) => (
            <li key={admin.user_id}>
              {admin.email}
              {userRole === 'admin' && admin.user_id !== currentUserId && (
                <button onClick={() => handleRemoveAdmin(admin.user_id)}>Remove</button>
              )}
            </li>
          ))}
        </ul>
      </div>
      <div className="vendor-section">
        <h2>Tables</h2>
        {userRole === 'admin' && (
          <div className="add-table">
            <button onClick={handleAddTable}>Add Table</button>
          </div>
        )}
        <ul>
          {tables.map((table) => (
            <li key={table.id} className={getStatusClassName(table.status)}>
              Table {table.number} - {table.status}
              {userRole === 'admin' && (
                <>
                  <button onClick={() => handleEditTable(table.id)}>Edit</button>
                  <button onClick={() => handleRemoveTable(table.id)}>Remove</button>
                </>
              )}
              {table.status !== 'occupied' && (
                <button onClick={() => handleToggleTableStatus(table.id, table.status)}>
                  {table.status === 'needs-service' ? 'Free Table' : 'Occupy Table'}
                </button>
              )}
              <button onClick={() => handleNavigateTableDetails(table.id)}>Details</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="vendor-section">
        <h2>Items</h2>
        {userRole === 'admin' && (
          <div className="add-item">
            <button onClick={handleAddItem}>Add Item</button>
          </div>
        )}
        <ul>
          {items.map((item) => (
            <li key={item.id}>
              {item.name} - ${item.price}
              {userRole === 'admin' && (
                <>
                  <button onClick={() => handleEditItem(item.id)}>Edit</button>
                  <button onClick={() => handleRemoveItem(item.id)}>Remove</button>
                </>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default VendorDetails;
