import React from 'react';
import '.../css/VendorDetails.css';

const TablesSection = ({
  tables,
  userRole,
  error,
  setIsEditingTable,
  setEditTableName,
  newTableName,
  setNewTableName,
  handleAddTable,
  handleRemoveTable,
  handleEditTable,
  fetchVendorTables,
}) => {
  return (
    <div className="tables-section card">
      <h2 className="section-title">Tables</h2>
      {userRole === '1' && (
        <div className="add-buttons">
          <button onClick={() => setNewTableName('') || setIsEditingTable(true)}>Add Table</button>
        </div>
      )}
      {/* Add your logic for displaying tables and handling actions */}
      {tables.map((table) => (
        <div key={table.id} className="table-card">
          <h3 className="table-name">{table.name}</h3>
          <div className="table-actions">
            <button onClick={() => handleEditTable(table)}>Edit</button>
            <button onClick={() => handleRemoveTable(table.id)}>Remove</button>
          </div>
        </div>
      ))}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default TablesSection;
