import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function AddTablePage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [tableNumber, setTableNumber] = useState('');
  const [status, setStatus] = useState('');
  const [error, setError] = useState(null);

  const handleAddTable = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8080/vendor/${id}/tables`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ number: tableNumber, status }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        const errorMessage = errorData.error || 'Failed to add table';
        setError(errorMessage);
        return;
      }

      navigate(`/vendor/${id}`);
    } catch (error) {
      setError('An unexpected error occurred while adding table');
    }
  };

  return (
    <div className="add-table-page">
      <h2>Add Table</h2>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={handleAddTable}>
        <label>
          Table Number:
          <input
            type="text"
            value={tableNumber}
            onChange={(e) => setTableNumber(e.target.value)}
            required
          />
        </label>
        <label>
          Status:
          <input
            type="text"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          />
        </label>
        <button type="submit">Add Table</button>
      </form>
    </div>
  );
}

export default AddTablePage;
